INSERT INTO `poems` (`title`, `verse_number`, `misra1`, `misra2`, `book_id`) VALUES
('onezeroone', 1011, 'zeroone', 'one', 101),
('قال قُرَيطُ بنُ أُنَيف أحد بني العنبر', 1, '', '', 1),
('قال الْفِنْدُ الزِّمَّاني في حرب البَسُوس', 2, ' فَلَيْتَ لِي بِهِمِ قَوْمًا إذَا رَكِبُوا ', ' شَدُّوا الإِغَارَةَ فُرْسَانًا وَرُكْبانَا )', 1),
('وقال أبو الغول الطَّهَويّ', 3, ' فَدَتْ نَفْسِ وَمَا مَلَكَتْ يَمِينِي ', ' فَوَارِسَ صَدَّقتْ فِيهِمْ ظُنُونِي )', 1),
('وقال جعفرُ بنُ عُلبةَ الحارثيّ', 4, ' فَقالُوا لنَا ثِنْتَانِ لاَ بُدَّ مِنْهُما ', ' صُدُورُ رِماحٍ اشُرِعَتْ أوْ سَلاَسِلُ )', 1),
('وقال أيضاً', 5, ' فَلاَ تَحسبي أنِّي تَخَشَّعْتُ بَعْدَكُمْ ', ' لِشَيْءٍ وَلاَ أنِّي مِنَ الْمَوْتِ أفْرقُ )', 1),
('قال ربيعة بن مَقروم الضبيّ ', 6, ' بِضَرْبَةٍ لَمْ تَكُنْ مِنِّي مُخَالِسَةً ', ' ولاَ تَعَجَّلْتُها جُبْناً ولاَ فَرَقَا )', 1),
('قال أبو كبير الْهُذَلِي ', 7, ' ممَّنْ حَمَلْنَ بِهِ وَهُنَّ عَوَاقِدٌ ', ' حُبُكَ النِّطَاقِ فَشَبَّ غَيْرَ مُهَبَّلِ )', 1),
('وقال تأبط شرًّا أيضاً', 8, ' يَحْمي الصِّحَابَ إِذَا تَكُونَ عَظِيمَةٌ ', ' وَإِذَا هُمُ نَزَلُوا فَمَأْوَى الْعُيَّلِ )', 1),
('وقال بعض بني قيس بن ثعلبة ', 9, ' إنَّا بني نَهْشَلٍ لا نَدَّعي لأَبٍ ', ' عَنْهُ وَلاَ هُوَ بِالأبْناءِ بَشْرِينَا )', 1),
('قال السَّمَوْأل بن عادِياءَ ', 10, ' وَنَرْكَبُ الكُرْهَ أحْياناً فَيَفْرِجُهُ ', ' عَنَّا الْحِفاظُ وَأسْيافٌ تُواتِينَا )', 1),
('قال الشَّميْذَرُ الحارثيُّ ', 11, ' بَنِي عَمِّنا لاَ تَذْكُروا الشِّعْرَ بَعْدَما ', ' دَفَنْتُمْ بِصَحْراءِ الْغُميْرِ الْقَوافِيا )', 1),
('وقال سَوَّارُ بنُ الْمُضَرَّب السَّعْدِيّ ', 12, ' إذَا اسْتُنْجِدُوا لَمْ يَسألُوا مَنْ دَعَاهُمُ ', ' لأَيَّةِ حَرْبٍ أمْ بِأَيِّ مَكانِ )', 1),
('وقال قَطَرِيُّ بن الْفُجاءَةِ المازِنيّ', 13, ' حَتَّى خَضَبْتُ بِما تَحَدَّرَ مِنْ دَمِي ', ' أَكْنافَ سَرْجِي أوْ عِنَانَ لِجَامِي )', 1),
('قال المبرد النثرة الدرع السابغة يقول درعي هذه تكفيني وقوله كل ', 14, ' وَالدِّرِْعُ لاَ ابْغِي بِهَا نَثْرَةً ', ' كلُّ امْرِئٍ مُسْتَوْدَعٌ مَالَه )', 1),
('وقال الأشْتَرُ النَّخَعِيُّ ', 15, ' أنَا ابنُ زَيَّابَةَ إنْ تَدْعُنِي ', ' آتكَ والظَّنُّ عَلَى الْكاذِبِ )', 1),
('وقال زُفَرُ بنُ الحَرِثِ ', 16, ' وَكُنَّا حَسِبْنا كُلَّ بَيْضاءَ شَحْمَةً ', ' لَيالِيَ لاَقَيْنَا جُذَامَ وَحِمْيَرَا )', 1),
('وقال عَمْرُو بْنُ مَعْدِ يكرِب الزُّبَيْدِيُّ ', 17, ' أقدم فيهم دعلجا وأكره ', ' إذا أكرهوا فيه الرماح تحمحما )', 1),
('وقال سيَّارُ بن قَصِيرٍ الطّائيّ ', 18, ' لَوْ شَهِدَتْ أمُّ الْقُدَيْدِ طِعَانَنَا ', ' بِمَرْعَشَ خَيْلَ الأَرْمَنِيِّ أرَنَّتِ )', 1),
('وقال أَنَيْفُ بنُ زَبّان النَّبْهانيُّ مِنْ طَيىء ', 19, ' إنْ تُذْنِبُوا ثُمَّ تَأتِيني بَقِيَّتُكُمْ ', ' فَمَا عَلَيَّ بِذَنْبٍ عِنْدَكُمْ فَوْتُ )', 1),
('وقال قيس بن الخَطيم ', 20, ' وَابْنُ صُبْحٍ سَادِراً يُوْعِدُنِي ', ' مَالَهُ فِي النَّاسِ مَا عِشْتُ مُجِيرُ )', 1),
('وقال الفَرَّارُ السَّلَميُّ ', 21, ' فَتَرَكْتُهُمْ تَقِصُ الرِّمَاحُ ظُهُورَهُمْ ', ' مِنْ بَيْنِ مُنْعَفِرٍ وَآخَرَ مُسْنَدِ )', 1),
('وقال الشُّدَاخُ بنُ يَعْمُرَ الْكِنَانِيّ ', 22, ' ذَكَرْت تَعِلَّةَ الْفِتْيَانِ يَوْماً ', ' وَالْحَلقَ الْمَلاَمَةِ بالْمُلِيم )', 1),
('وقال رجل مِنْ بني عُقَيْل', 23, ' لَهَا لَوْنٌ مِنَ الهَامَاتِ كَابٍ ', ' وَإنْ كَانَتْ تُحَادَثُ بِالصِّقَالِ )', 1),
('وقال قيس بن زهير بنَ جذِيمة العبْسيّ فِي قَتْلِهِ حَمَلَ بن بدر يومَ جفر الهبَاءَة ', 24, ' وَلَمَّا رَأيْتُ أنَّنِي قَدْ قَتَلْتُهُ ', ' نَدِمْتُ عَلَيْهِ أيَّ سَاعَةِ مَندَمِ )', 1),
('وقال رجل من بني تميم ', 25, ' سَليلَةُ سَابِقَيْنِ تَنَاجَلاَهَا ', ' إذَا نُسِبَا يَضُمُّهُما الْكُرَاعُ )', 1),
('وقال بعض بني فَقْعَس ', 26, ' فَهَلاَّ أَعَدُّونِي لِمِثْلِي تَفَاقَدُوا ', ' إذَا الخَصْمُ أبْزَى مَأئِلُ الرَّأسِ أنْكَبُ )', 1),
('قالت كبشة أخت عمرو بن معد يكرب ', 27, ' ودَعْ عَنْكَ عمْراً إنَّ عَمْرًا مُسَالِمٌ ', ' وَهَل بَطْنُ عَمْرٍ وغَيْرُ شِبْرٍ لِمَطْعَمِ )', 1),
('وقال الأحوص بن محمد بن عاصم بن ثابت بن أبي الأفلح الأنصاري ', 28, ' إذَا أبْصَرْتَنِي أعْرَضْتَ عَني ', ' كَأنَّ الشَّمْسَ مِنْ قِبَلِي تَدُورُ )', 1),
('وقال الطِّرِمّاح بْن حَكيم ', 29, ' كُلٌّ لَهُ نِيَّةٌ فِي بُغْضِ صَاحِبِه ', ' بِنِعْمةِ اللهِ نَقْليكُمْ وَتَقْلُونَا )', 1),
('وقال بعض بني فقعس ', 30, ' نَاسَيْتُهمْ بَغْضاءَهُمْ وتَرَكْتُهمْ ', ' وَهُمُ إذَا ذَكِرَ الصَّدِيقُ أعادِي )', 1),
('وقال جابر بن رَأْلاَن السَّنْبِسِيّ ', 31, ' فإِن تُبْغِضُونَا بِغضَةً فِي صُدُورِكُمْ ', ' فإِنَّا جَدَعتَا مِنْكمُ وشَرَينا )', 1),
('وقال آخر من بني فقعس ', 32, ' فإِنْ تَغْمِزْ مَفَاصِلَنا تَجِدْهَا ', ' غِلاَظاً فِي أنَامِلِ مَنْ يَصولُ )', 1),
('وقال مِسوَرُ بن زيادةَ الْحارثي', 33, ' أبَعْدَ الَّذِي بِالْنَّعْفِ نَعْفِ كُوَيْكِبٍ ', ' رَهِينَةِ رَمْسٍ ذِي تُرَابٍ وَجنْدَلِ )', 1),
('وقال آخر ', 34, ' اللُّؤْمُ أكْرَمُ منْ وَبْرٍ وَوالِدِهِ ', ' وَاللُّؤْمُ أكْرَمُ مِنْ وَبْرٍ ومَا ولَدَا )', 1),
('وقال حُرَيثُ بنُ عنَّاب النبهانِيّ ', 35, ' تَعَالَوْا أفَاخِرْكُمْ أَأَعْيا وَفَقْعَسٌ ', ' إلَى الْمَجْدِ أَدْنَى أَمْ عَشِيرَةُ حَاتِمِ )', 1),
('وقال آخر', 36, ' وكمْ دَهِمتْنِي مِنْ خُطُوبٍ مُلِمَّةٍ ', ' صَبَرْتُ عَليْها ثُمَّ لَمْ أتَخَشَّعِ )', 1),
('وقال بعض بني عبد شمس بن فقعس', 37, ' لاَذَتْ هُنَالِكَ بِالأشْعافِ عالِمَةً ', ' أنْ قدْ أطاعَتْ بِلَيْلٍ أمْرَ غاوِيها )', 1),
('وقال آخر ', 38, ' وَفارَقْتُ حتى مَا أُبَالِي مِنَ النَّوَى ', ' وَإنْ بَانَ جِيرَانٌ عَليَّ كِرَامُ )', 1),
('وقال آخر', 39, ' أقلب عيني لا أرى من أحبه ', ' وفي الدار ممن لا أحب كثير )', 1),
('وقال عمرو بن شأْس ', 40, ' فَإنْ كُنْتِ مِنِّي أوْ تُرِيدِينَ صُحْبَتِي ', ' فَكُونِي لَهُ كَالسَّمْنِ رُبَّتْ لهُ الأَدَم )', 1),
('وقال حِطَّانُ بنُ الْمُعَلَّى ', 41, ' أنْزَلَنِي الدَّهْرُ عَلى حُكْمِهِ ', ' مِنْ شَامِخٍ عَالٍ إلى خَفْضِ )', 1),
('قال أبو هلال هكذا قال أبو تمام ونحن نقول هو حيان بن عليق بن ربيعة الطائي أخو بني أخزم ينتهي نسبه إلى عمرو بن ثعل وهو شاعر جاهلي ', 42, ' أنا أبُو بَرْزَةَ إذْ جَدَّ الْوَهَلْ ', ' خُلِقْتُ غَيْرَ زُمَّلٍ وَلاَ وَكَلْ )', 1),
('وقال رجل من بني كلب', 43, ' فَإِنِّي مِثْلُ مَا تَجِدِينَ وَجْدِي ', ' وَلكنْ أصْبَحَتْ عَنْهُمْ قَرُونِي )', 1),
('قال أبو حنْبل الطائي ', 44, ' قَدْ كانَ سَيرٌ فَحُلَّوا عَنْ حَمُولَتِكُمْ ', ' إنِّي لِكُلّ امْرِئٍ مِنْ جَارهِ جَارُ )', 1),
('وقال جابر بن الثعلب الطائي', 45, ' وَمنْ يَفْتقرْ في قَوْمِهِ يَحْمَدِ الْغَنى ', ' وإنْ كانَ فِيهمْ وَاسِطَ الْعَمّ مُخْوِلا )', 1),
('وقال الراعي', 46, ' فَباتَ يُرِيهِ عِرْسَهُ وَبَناتِهِ ', ' وبِتُّ أُردِيهِ النَّجْمَ أيْنَ مَخافِقُهْ )', 1),
('وقال أبو النَّشْناش ', 47, ' لِيُكْسِبَ مَجْداً أوْ لِيُدْرِكَ مَغْنماً ', ' جَزِيلاً وَهَذَا الدَّهْرُ جَمٌّ عَجاَئِبُهْ )', 1),
('وقال آخر ', 48, ' فإِمَّا تَرَيني الْيَوْمَ أصْبَحتُ بَادِناً ', ' لَدَيْكِ فَقدْ أُلْفَيَ عَلى الْبُزْلِ مِرْجَمَا )', 1),
('قال أبو رياش هذا غلط من أبي تمام لأن يحيى ابن منصور ذهلي وإنما هذه الأبيات لموسى بن جابر الحنفي وكلاهما شاعر إسلامي مجيد وسيأتي لموسى بن جابر ذكر ', 49, ' وَجَدْنا أبانا كانَ حَلّ بِبَلْدَةٍ ', ' سِوًى بَيْنَ قَيْسٍ قَيْسِ عَيْلاَنَ وَالفِزْرِ )', 1),
('وقال بعض بني عبس', 50, ' وأخْلاَقَنا إعْطَاءَنَا وإبَاءَنَا ', ' إذا مَا أبَيْنا لاَ نَدُرُّ لَعَاصِبِ )', 1),
('وقال حسان بن نشْبَةَ العدوي في ذلك ', 51, ' وكَمْ تَركْنا هُناكَ مِنْ بَطَلٍ ', ' تَسْفِي عَلَيْهِ الرِّيَاحُ فِي لِمَمِهْ )', 1),
('وقال في ذلك هلال بن رزين أحد بني ثور بن عبد مناة بن أُدّ', 52, ' وَبالبَيْدَاءِ لمَّا أنْ تَلاَقَتْ ', ' بِها كَلْبٌ وَحَلَّ بها النُّذُورُ )', 1),
('قال جَزْءُ بن ضِرار أخو الشماخ ', 53, ' فإِنْ يَكُ حَقًّا مَا أتَاني فإِنَّهُمْ ', ' كرَامٌ إذَا مَا النَّائِباتُ تَنُوبُ )', 1),
('قال حُجْر بن خالد بن محمود بن عمرو بن مَرْثِدِ بن مالك بن ضُبَيْعَةَ بن قيس بن ثعلبة ', 54, ' وإذَا هلكْتُ فَلاَ تُريدِي عاجِزاً ', ' غُسًا وَلا بَرَماً ولاَ مِعْزَالاَ )', 1),
('وقال جعفر بن عُلبَة الحارثيّ حين لقي بني عُقَيْل وقد تقدمت ترجمته ', 55, ' مَن يَلْقِني يُودِ كمَا أَوْدَتْ إرَم ', ' )', 1),
('وقال البُرْج بن مُسْهر الطائي ', 56, ' إذا كنت في قوم عدا لست منهم ', ' ) أي وأنت لا تهوى هواهم فكل مما علفت وهذا من الأمثال', 1),
('وقال آخر من بني أسد في يوم اليمامة', 57, ' أقُولُ لِنَفْسي حِينَ خَوَّدَ رأْلُها ', ' مَكانَكِ لَمَّا تُشْفِقِيِ حينَ مُشْفَق )', 1),
('وقال أيضاً', 58, ' ألَمْ ترَياَ أنِّي حَمَيْتُ حَقيقَتيِ ', ' وَباشَرْتُ حَدَّ الْمَوْتِ وَالْمَوْتْ دُونُهاَ )', 1),
('وقال البَعيثُ بن حُرَيْث ', 59, ' فَقُلْتُ لهُ أهْلاً وسَهْلاً وَمَرْحَباً ', ' فَرَدَّتْ بِتَأْهيلٍ وَسَهْلٍ وَمَرْحَبِ )', 1),
('وقال المُثَّلم بن رياح بن ظالم المُرّى ', 60, ' مَنْ مُبْلِغٌ عَنّى سِنَاناً رِسالةً ', ' وِشِجْنةَ أنْ قُوما خُذَا الْحَقَّ أوْدَعا )', 1),
('قال حصين بن حمام المرّي ', 61, ' لعمرك ما ضلت ضلال ابن جوشن ', ' حصاة بليل ألقيت وسط جندل )', 1),
('وقال بَشَامة بن حَزْن ', 62, ' إِنِّي امْرُؤٌ اسِمُ الْقَصائِدَ لِلْعِداَ ', ' إنَّ الْقصائِدَ شَرُّها أغْفَالُهَا )', 1),
('وقال عَقيل بن عُلَّفَةَ المرّي ', 63, ' تَنَاهَوْا وَاسْأَلُوا ابْنَ أَبِي لَبيدٍ ', ' أَأَعْتَبَهُ الضُّبارِمَةُ النَّجيدُ )', 1),
('وقال آخر', 64, ' فَدَامَ لِي وَلَهُمْ ما بِي وَما بِهِمِ ', ' وَماتَ أكْثرُنا غَيْظاً بِما يَجِدُ )', 1),
('وقال طَرَفةُ الخُزيميُّ ', 65, ' وَما غَمَراتُ المَوْتِ إلاّ نِزَالُكَ الْكَمَّى ', ' عل لَحمِ الْكَمِيِّ المقَطَّر )', 1),
('وقال أيضا', 66, ' وَإنَّ نِجَاري يَا ابْنَ غَنْمٍ مُخَالِفٌ ', ' نِجارَ اللِّئَامِ فَابْغنِي منْ وَرائِيا )', 1),
('وقال عُروة بن الوَرْد ', 67, ' لَحَا اللهُ صُعْلُوكاً إذَا جَنَّ لَيْلُهُ ', ' مُصَافِي المُشَاشِ آلِفاً كلَّ مَجْزَر )', 1),
('وقال عنترة تقدمت ترجمته', 68, ' تَرَكْتُ بَني الْهُجَيْمِ لَهُمْ دَوَارٌ ', ' إذَا تمْضِي جَمَاعَتُهُمْ تعود )', 1),
('وقال أيضاً', 69, ' أتَشْحَذُ أرْماحاً بِأَيْدِي عَدُوِّنا ', ' وتَتْرُكُ أرْحاماً يِهنَّ تُكَابِدُ )', 1),
('قال أبو الفتح الشارق اسم صنم لهم ولذلك قالوا عبد الشارق كما قالوا عبد العزى والعزى صنم أيضا ومثل ذلك عبد يغوث وعبد ود ونحوه ', 70, ' ألا حُيِّيِتِ عَنَّا رُدَيْنا ', ' نُحيَيّيهاَ وَإِنْ كَرمَتْ عَلَيْنا )', 1),
('قال بِشْرُ بنُ أبَيّ بن حمام العبسي لبني زهير بن جذيمة ', 71, ' لُطِمْنَ عَلى ذَاتِ الإِصادِ وجَمْعُكُمْ ', ' يَروْنَ الأَذَى منْ ذِلَّةٍ وَهوانِ )', 1),
('وقال المساور بن هند بن زهير', 72, ' وَأرَى الْغَوَانِيَ بَعْدَماَ أوْجَهْنَني ', ' أعْرَضْنَ ثُمَّتَ قُلْنَ شَيْخٌ أعْوَرُ )', 1),
('وقال عُرْوَةُ بن الوَرْدِ الْعَبْسِي ', 73, ' وَلَنا قَنَاةٌ مِنْ رُدَيْنَةَ صَدْقَةٌ ', ' زَوْرَاءُ حَامِلُهَا كَذَلِكَ أزْوَرُ )', 1),
('قال هُدْبة بن خَشرَم ', 74, ' إنّي مِنْ قُضَاعة مَنْ يَكِدْها ', ' أكِدْهُ وَهْيَ مِنِّي في أمان )', 1),
('وقال عبد الله بن سبرة الحرَشي ', 75, ' إنِّي امْرُؤٌ مِنْ تَنُوخَ ناصِرُهُ ', ' مُحْتَمِلٌ فِي الْحُرُوبِ مَا احْتَمَلُوا )', 1),
('وقال الشَّنْفَرى الأزدي', 76, ' إذا نَفَرَتْ مِنْ بَيَاضِ السُّيُوفِ ', ' قُلْنا لَها أقْدِمي مُقْدَمَا ', 1),
('قال تأبط شرًّا ', 77, ' قَلِيلُ غِرَارِ النَّوْمِ أكْبَرُ هَمِّهِ ', ' دَمُ الثَّأْرِ أوْ يَلْقَى كَمِيًّا مُسَفّعا )', 1),
('وقال بعض بني قيس بن ثعلبة', 78, ' إذا ما قُلُوبُ الْقَوْمِ طارَتْ مَخَافَةً ', ' مِنَ الْمَوْت أرْسَوْا بِالنفُوسِ الْمَوَاجِدِ )', 1),
('قال جَحْدَرُ بنُ ضُبَيْعَةَ بن قيس بن ثعلبة ', 79, ' قَدْ عَمِلَتْ وَالِدَةٌ مَا ضَمَّتِ ', ' مَا لَفَّفَتْ فِي خِرَق وَشَمَّتِ )', 1),
('وقال حجر بن خالد الثعلبي ', 80, ' وجَدْنا أبَانا حَلَّ فِي الْمَجْدِ بَيْتُهُ ', ' وأَعْيا رِجَالاً آخَرِينَ مَطالِعُهُ )', 1),
('وقال حجر بن خالد أيضا', 81, ' غَدَاةَ أتَاهُ جَبَّارٌ بِإِدٍّ ', ' مُعَضِّلةٍ وَحَادَ عَنْ الْقتَالِ )', 1),
('وقال بعض بني جُهَيْنَة في وقعة كلب وفَزارة ', 82, ' فَإِنَّ ابْنَ أخْتِ الْقَوْمِ مُصْغًى إنَاؤُهُ ', ' إذَا لَمْ يُزَاحِمْ خَالهُ بِأَبٍ جَلْدِ )', 1),
('وقال سُلْميُّ بنُ رَبيعة من بني السِّيد بن ضَبَّةَ ', 83, ' كَأنَّ عَلَى سَوَاعِدِهِنَّ وَرْساً ', ' عَلاَ لَوْنَ الأشَاجِعِ أوْ خِضَابَا )', 1),
('وقال أيضاً', 84, ' فِدى لِفَتًى ألْقَى إلَيَّ بِرَأْسِها ', ' تِلاَدِي وَأهْلي مِنْ صَدِيقٍ وَجَامِل )', 1),
('وقال حُسَيْل بن سُجَيْح الضبي ', 85, ' وأرْهَبْتُ أُولَى الْقَوْمِ حَتَّى تَنَهْنَهُوا ', ' كَما ذُدْتَ يَوْمَ الْوِرْدِ هِيماً خَوَامِسا )', 1),
('وقال مُحْرزَ بنُ المكعْبر الضَّبيّ ', 86, ' حَتَّى أتى عَلمَ الدَّهْنا يُوَاعِسُهُ ', ' وَاللهُ أعْلمُ بالصَّمَّانِ مَا جَشِمُوا )', 1),
('وقال أبو ثمامة بن عازب الضبي ', 87, ' أُخَاصِمُهُمْ مَرَّةً قَائِمًا ', ' وأجْثُوا إذَا مَا جَثَوْا لِلرُّكَبْ )', 1),
('وقال عبد الله بن عَنَمَةَ الضبي ', 88, ' تحية بينهم ضرب وجيع ', ' ) فالضرب لا يكون تحية', 1),
('وقال الفضل بن الأخضر بن هبيرة الضبي', 89, ' ولاَ تَكُونَنْ كَمُجْرَى دَاحِسٍ لَكُمُ ', ' فِي غَطَفانَ غَدَاةَ الشِّعْبِ عُرْقُوبُ )', 1),
('وقال إياسُ بنُ مالكِ بن عبدِ اللهِ بن خَيْبَرَى الطائي', 90, ' إذْ لا تَخَافُ حُدُوجُنَا قَذَفَ النَّوَى ', ' قَبْلَ الْفَسادِ إقامَةً وَتَدَيُّرَا )', 1),
('وقال عبد الرحمن المَعْنيُّ ', 91, ' ثمانُونَ ألْفاً وَلَمْ أُحْصِهِمْ ', ' وَقَدْ بَلَغَتْ رَجْمَها أوْ تَزِيدُ )', 1),
('وقال جابر بن رألان السّنبسي', 92, ' تَجَوَّدْتُ فِي مَجْلِسٍ وَاحِدٍ ', ' قِرَاها وَتِسْعينَ أمْثَالَها )', 1),
('قال قبيصة بن النصراني الجرمي من طيىء ', 93, ' عَشِيَّةَ قَطَّعْنا قَرَائِنَ بَيْنَنا ', ' بِأَسْيافِنَا وَالشَّاهِدُونَ بَنُو بَدْرِ )', 1),
('وقال خُفافُ بن نَدَبةَ ', 94, ' يَزيدُ نَبَالَةً عَنْ كلِّ شَيْءٍ ', ' وَنَافِلَةً وَبَعْضُ الْقوْمِ دُونُ )', 1),
('وقال معبد بن علقمة ', 95, ' فَقُلْ لِزُهَيْرٍ إنْ شَتَمْتَ سَرَاتَنا ', ' فَلَسْنَا بِشَتَّامِينَ لِلْمُتَشَتِّمِ )', 1),
('وقال حريث بن عناب بن مطر بن سلسلة بن كعب بن عوف تقدمت ترجمته', 96, ' لمَّا رَأيْتُ الْعَبْدَ نَبْهَانَ تَارِكي ', ' بِلَمَّاعَةٍ فِيهَا الْحَوادِثُ تَخْطُرُ )', 1),
('وقال أبان بن عبدة', 97, ' بِبِيضٍ خِفَافٍ مُرْهَفَاتٍ قَوَاطِعٍ ', ' لِدَاوُدَ فِيهَا أثْرُهُ وَخَوَاتِمُهْ )', 1),
('قال أبو هلال قراد بن عباد وقع هكذا في الأصل وهو خطأ وإنما هو قراد بن العيار بن محرز بن خالد أحد بني رزام وأبو العيار أحد شياطين العرب وقراد شاعر إسلامي مقل ', 98, ' وَلَمْ يَحْبُهُ بِالنصْرِ قَوْمٌ أعِزَّةٌ ', ' مَقَاحِيمُ فِي الأَمْرِ الَّذِي يُتَهَيَّبُ )', 1),
('وقال الفرزدق ', 99, ' لاَ قَوْمَ أكْرَمُ مِنْهُمْ يَوْمَ قالَ لَهُمْ ', ' مُحَرِّضُ الْمَوْتِ عَنْ أحْسَابِكُمْ ذُودُوا )', 1),
('وقال آخر', 100, ' قدْ عَلِمَ الْمُسْتِأْخِرُونَ في الْوَهلْ ', ' إذا السُّيُوفُ عُرِّيَتْ مِنَ الْخِللِ )', 1),
('وقال درَّاج وكان قد طعن', 101, ' هِيمٌ بِهيمٍ طُلِيَتْ تَمَرَّسْ ', ' )', 1),
('وقال سَوَّار', 102, ' سَعةَ الطّريقِ مَخافة أنْ يُؤْسَرُوا ', ' والخَيْلُ تَتْبَعُهُمْ وهُمْ فُرَّارُ )', 1),
('وقال أوس بن ثعلبة', 103, ' جَذّامُ حَبْلِ الْهَوَى ماضٍ إذَا جَعلَتْ ', ' هَواجِسُ الْهَمِّ بَعْدَ النَّوْمِ تَعْتَكِرُ )', 1),
('وقال الهُذْلول بن كعب العنبري ', 104, ' تَقولُ وصَكَّتْ نَحْرَها بِيَميِنَها ', ' أبَعْلِيَ هَذَا بِالرَّحَا الْمُتَقاعِسُ )', 1),
('وقالت كنزة أم شملة بن برد المِنقَري', 105, ' إنْ يَكُ ظَنِّي صَادِقاً وَهْوَ صادِقِي ', ' بِشَمْلَةَ يَحْبِسْهُمْ بِهَا مَحْبِساً أزْلاً )', 1),
('وقال قَبيصة بن جابر ', 106, ' أقِيمُوا صُدُورَ الْخَيْلِ إنَّ نُفُوسَكُمْ ', ' لِميقَاتِ يوْمٍ مَا لَهُنَّ خُلوفُ )', 1),
('وقال سالم بن وابصة ', 107, ' وَمَوْقِفٍ مِثْلَ حَدِّ السَّيْفِ قُمْتُ بِهِ ', ' أحْمي الذِّمارَ وَترْمِيني بهِ الْحَدَقُ )', 1),
('وقال الأخنس ', 108, ' تُمَشِّي بِهَا حُولُ النَّعَامِ كَأَنَّهَا ', ' إمَاءٌ تُزَجَّى بِالْعَشِيِّ حَوَاطِبُ )', 1),
('وقال العُدَيل بن الفُرْخ العجليُّ ', 109, ' بني قبة الإسلام حتى كأنما ', ' هدى الناس من بعد الضلال رسول )', 1),
('وقال عبد القيس بن خفاف البُرْجُميّ ', 110, ' صَحَوْتُ وَزَايَلَني باطِلِي ', ' لَعَمْرُ أبيكَ زِيَالاَ طَوِيلاَ )', 1),
('قال أبو رياش هي من بني قشير ', 111, ' فإِنْ يَكُ ظَنِّي صادِقاً وَهْوَ صادِقي ', ' بِكُمْ وَبِأَحْلاَمٍ لَكُمْ صَفِراتِ )', 1),
('وقال ابن السليماني ', 112, ' لَعَمْري لَقَدْ كَانَتْ فِجَاجٌ عَريضَةٌ ', ' ولَيْلٌ سخامِيُّ الْجَناحَينِ أدْهَمُ )', 1),
('وقال قتادة بن مَسلمة الحَنفي ', 113, ' يَمْلأُ عَيْنَيْكَ بِالْفِناءِ ويُرْضِيكَ ', ' عِقاباً إنْ شِئْتَ أوْ نزَقا )', 1),
('قال جُرَيْبَةُ بن الأشْيم الفَقْعَسيُّ ', 114, ' إذَا الْخَيْلُ صاحَتْ صيِاحَ النُّسُورِ ', ' حَزَزْنا شَراسِيفَها بِالْجِذَمْ )', 1),
('وقال أبو العطاء السندي ', 115, ' فإنْ تُمْسِ مَهْجُورَ الْفِناءِ وَرُبَّما ', ' أقامَ بهِ بَعْدَ الْوُفُودِ وُفُودُ )', 1),
('وقال رجل من خثعم ', 116, ' نَهِلَ الزَّمَانُ وَعَلَّ غَيْرَ مُصَرَّدِ ', ' مِنْ آلِ عِتَّابٍ وَآلِ الأسْوَدِ )', 1),
('وقال أيضًا', 117, ' أقُولُ وَما يَدْري أُناسٌ غَدْوَا بِهِ ', ' إلى اللّحدِ ماذَا أدْرَجُوا فِي السَّبائِبِ )', 1),
('وقال تأبط شرًّا ', 118, ' قَسَمْنا بِذَاكَ الدَّهرَ شطْرَيْنِ بَيْنَنا ', ' فَما يَنْقَضي إلاَّ وَنَحْنُ عَلى شَطْرِ )', 1),
('وقال سُيَودُ المرَاثِدِ الحارثي', 119, ' فَتًى قَبَلٌ لمْ تُعْنِسِ السِّنُّ وَجْهَهُ ', ' سِوَى خُلْسةٍ في الرّأْسِ كالبَرْقِ في الدُّجَى )', 1),
('وقال الحريث بن زيد الخيل ', 120, ' بأشَدِّهِمْ كَلَباً عَلى أعْدَائِهِمُ ', ' وَأعَزِّهِمْ فَقْداً عَلى الأصْحابِ )', 1),
('وقال أبو حِناك البراءُ بنِ ربعيّ الفقعسي', 121, ' ثَمانَيِةٌ كانُوا ذُؤَابَةَ قَوْمِهِمْ ', ' بِهِمْ كُنْتُ أُعْطِي ما أشَاءُ وأمْنَعُ )', 1),
('وقال بعض بني أسد', 122, ' بَكّى عَلى قَتْلَى العَدَانِ فإنّهُمْ ', ' طَالَتْ إقامَتُهُمْ بِبطْنِ بَرَامِ )', 1),
('وقال الشَّمَرْدَلُ بنُ شَرِيك ', 123, ' وَلَوْلاَ الأُسَى مَا عِشْتُ في النَّاسِ سَاعَةً ', ' ولَكنْ إذ مَا شِئتُ جاوَبَنيِ مِثْلي )', 1),
('قال الأسود بن عبد يغوث بن المطلب بن نوفل ', 124, ' ألاَ قَدْ سادَ بَعْدَهُمُ رِجالٌ ', ' وَلَوْلاَ يَوْمُ بَدْرٍ لَمْ يَسُودُوا )', 1),
('وقال عبد الملك بن عبد الرحيم الحارثي يكنى أبا الوليد ', 125, ' فكُنْتُ كَمَغْلُوبٍ عَلى نَصْلِ سَيْفِهِ ', ' وَقَدْ حَزَّ فِيهِ نَصْلُ حَرَّانَ ثَائرِ )', 1),
('وقال عُتيّ بنُ يزيد بن مالك العُقيليّ', 126, ' أعَدَّاءُ ما وَجْدِي عَليكَ بِهَيِّن ', ' وَلا الصَّبْرُ إنْ أُعْطِيتُهُ بجَميل )', 1),
('وقال خلف بن خليفة ', 127, ' أُعاتِبُ نَفْسِي أنْ تَبسَّمْتُ خالِياً ', ' وَقدْ يَضْحَكُ المَوْتُورُ وَهْوَ حزيِنُ )', 1),
('وقال أرْطأة بنُ سُهيّة المُرّيُّ ', 128, ' أخِلاَّيَ لَوْ غَيْرُ الحَمامِ أصابَكُمْ ', ' عَتَبْتُ وَلكِنْ ما عَلَى المَوْتِ مَعْتَبُ )', 1),
('وقال آخر في ابن له', 129, ' أُلاَمُ عَلى تَبَكِّيهِ ', ' وَألْسُهُ فلاَ أجِدُهْ )', 1),
('قال محمد ابن سلام الصحيح أن هذه الأبيات لعمرو بن شقيق أحد بني فهر بن مالك ومن الناس من يرويها لكرز بن حفص بن الأخيف العامري وعمرو بن شقيق أولى بها وهذا الشعر قيل في قتل ربيعة بن مكدم الكناني أحد فرسان مضر المعدودين وشجعانهم المشهورين قتله نبيشة بن حبيب السلمي في يوم الكديد ', 130, ' لوْلا السِّفارُ وبُعْدُ خَرْقٍ مَهْمَةٍ ', ' لتَرَكْتُها تَحْبُو بأرْبَعةٍ عَلى العُرْقُوبِ )', 1),
('وقالت أيضاً', 131, ' وَإذا دَعَتْ قُمْريَّةٌ شَجَناً لَهَا ', ' يوْماً عَلى فَنَنِ دَعَوْتُ صَباحِي )', 1),
('وقال الحجناء مولى بني أسد', 132, ' أعاذِلَ مَنْ يُرْزَأْ كَحَجْناءَ لاَ يَزَلْ ', ' كئيباً وَيزهَدْ بَعْدَهُ في العَوَاقِب )', 1),
('وقال أبو الشَّغْب العبسي في خالد بن عبد الله القسري ', 133, ' لعمْرُكَ ما وَارَى التُّرَابُ فَعالَهُ ', ' وَلكِنَّما وَرَاى ثِياباً وأعْظُما )', 1),
('وقال آخر', 134, ' تَظَلُّ بَنَاتُ الْعَمِّ وَالْخَالِ حَوْلَهُ ', ' صَوَادِيَ لاَ يَرْوَيْنَ بِالْبارِدِ الْعَذْبِ )', 1),
('وقال الحسين بن مطير بن الأشيم الأسدي ', 135, ' قلدته عرى الأمور نزار ', ' قبل أن يهلك السراة البحور )', 1),
('وقال أشجع بن عمرو السلمي في محمد بن منصور بن زياد ', 136, ' أنْعَى فَتَى الْجُودِ إلَى الْجُودِ ', ' مَا مِثْلُ مَنْ أنْعَى بمَوْجُودِ )', 1),
('وقال مسلم بن الوليد ', 137, ' فَلاَ وَجْدَ حَتَّى تَنْزفَ الْعَيْنُ مَاءَهاَ ', ' وَتَعْتَرِفَ الأَحْشَاءُ بِالْخَفَقانِ )', 1),
('وقالت صفية الباهلية ترثي أخاها', 138, ' لَوْ أنَّ خَيْرَكَ كانَ شَراًّ كُلُّهُ ', ' عِنْدَ الذِينَ عدَوْا عَلَيْكَ لَما عَدَا )', 1),
('وقال يزيد بن عمرو الطائي', 139, ' وقائِلَةٍ مَنْ أمَّها طَالَ لَيْلُهُ ', ' يَزِيدُ بْنُ عَمْرٍو أمّها فَاهْتَدَى لَها )', 1),
('قال سليمان بن قتَّة العَدوي ', 140, ' وَكانوا غِياثاً ثُمَّ أضْحَوْا رَزِيَّةً ', ' ألا عَظُمَتْ تلْكَ الرَّزَايَا وَجَلَّتِ )', 1),
('وقال النابغة الْجَعْدِيُّ ', 141, ' وَالنَّضْر أقْرَبُ مَنْ أصَبْتَ وَسيلةً ', ' وأحَقُّهُمْ إنْ كانَ عِتْقٌ يُعْتَقُ )', 1),
('وقال آخر', 142, ' لَعَمْرِي لَئِنْ سُرَّ الأعادِي فَأظْهَرُوا ', ' شَمَاتاً لَقدْ مَرُّوا بِرَبْعِكَ خَالِيَا )', 1),
('وقال كعب بن زهير ', 143, ' إذَا انْتَضلَ الْقَوْمُ الأحَادِيثَ لَمْ يَكُنْ ', ' عَيِيا وَلاَ رَبًّا عَلى مَنْ يُقَاعدُ )', 1),
('وقال آخر', 144, ' خَفيِفَ الْحَاذِ نَسَّالَ الْفَيافِي ', ' وَعَبْداً لِلصَّحابَةِ غَيْرَ عَبْدِ )', 1),
('وقال آخر ', 145, ' أضْحَى قِرًى لِلْمَنايا رَهْنَ بَلْقَعةٍ ', ' وقَدْ يَكُونُ غَداةَ الرَّوْعِ يَقْريها )', 1),
('وقال الربيع بن زياد في مالك بن زهير العبسي ', 146, ' أولاَكَ بَنو خَيرٍ وَشّرٍ كِلَيْهِما ', ' جَميعاً ومَعْروفٍ ألَمَّ ومُنْكَرِ )', 1),
('وقال غُويَّةُ بنُ سُلْميِّ بن ربيعةَ', 147, ' وبعْد أبي ربيعة عبدِ عمروٍ ', ' ومسْعُودٍ وبعد أبي هلال )', 1),
('وقال المِسْجاحُ بنُ سِباع الضَّبيُّ ', 148, ' وَشَهْرٌ مُسْتَهَلٌّ بَعْدَ شَهْرٍ ', ' وحَوْلٌ بَعْدَهُ حَوْلٌ جَدِيدُ )', 1),
('وقال زُوَيْهِرُ بنُ الحرث بن ضِرار ', 149, ' وَكَانَ عَمِيدَنا وَبَيْضَةَ بَيْتِنا ', ' فَكُلُّ الَّذِي لاَقَيْتُ مِنْ بَعْدِهِ جَلَلْ )', 1),
('وقال إياس بن الأرتِّ ', 150, ' وَحانَ فِراقٌ مِنْ أخٍ لكَ ناصِحٍ ', ' وَكانَ كَثيرَ الشَّرِّ لِلْخَيْرِ تَوْأمَا )', 1),
('وقال الغَطَمَّشُ من بني شقرة بن كعب بن ثعلبة بن سعد بن ضبة', 151, ' عَلى رِشْدَةٍ مِنْ أُمِّهِ أوْ لِغَيَّةِ ', ' فَيَغْلِبَها فَحْلٌ عَلى النَّسْلِ مُنْجِبُ )', 1),
('قال أبو هلال في الشعراء ثلاثة يقال لهم القلاخ أحدهم القلاخ الراجز بن حزن بن جناب بن منقر والآخر القلاخ بن زيد أحد بني عمرو ابن مالك والثالث القلاخ العنبري أحد بني العنبر والمراد هنا القلاخ بن حزن المنقري وهو شاعر إسلامي مجيد مقل ', 152, ' وكنت إذا ما شئت سنيت والدا ', ' يزين كما زان اليدين الأساور )', 1),
('وقال الضَّبيّ', 153, ' وَلَرُبَّ عَانٍ قَدْ فَكَكْتَ وَسائِلٍ ', ' أعْطَيْتَهُ فَغَدَا وَأنْتَ حَمِيدُ )', 1),
('وقال لبيد ', 154, ' أخاً لَي أمَّا كُلُّ شَيءٍ سَألْتُهُ ', ' فَيُعْطِي وَأمَّا كُلَّ ذَنْبٍ فيغْفِرُ )', 1),
('وقال مُنْقِذٌ الهِلاَلِيُّ ', 155, ' وَلخَيْرُ حَظِّكَ فِي المُصِيبَةِ أن ', ' يَلْقاكَ عِنْدَ نُزُولِها الصَّبْرُ )', 1),
('وقال رجل من بني أسد', 156, ' أبْعَدْتَ مِنْ يَوْمِكَ الْفِرَارَ فَما ', ' جاوزْتَ حَيْثُ انْتَهَى بِكَ الْقدَرُ )', 1),
('وقال النابغة الجعدي تقدمت ترجمته', 157, ' فتًى تَمَّ فِيهِ مَا يَسُرُّ صَدِيقَهُ ', ' عَلى أنّ فِيه مَا يَسُوءُ الأعَادِيَا )', 1),
('وقال ابن أُهْبانَ الفقعسيُّ يرثي أخاه', 158, ' إذَا نازعَ الْقَوْمَ الأحادِيثَ لَمْ يَكُنْ ', ' عَيِيًّا ولاَ رَبًا عَلى مَنْ يُقاعِدُ )', 1),
('وقال رجل من كلب', 159, ' لَحَا اللهُ دَهْرًا شَرُّهُ قَبْلَ خَيرِهِ ', ' وَوجدًا بَصَيْفِيٍّ أتَى بَعْدَ مَعْبَدِ )', 1),
('قال الأبَيرِدُ اليَرْبوعي ', 160, ' وَسامَى جَسيمَاتِ الأُمُورِ فَنالَها ', ' عَلى الْعُسْرِ حتَّى أدْرَكَ الْعُسُرَ الْيُسْرُ )', 1),
('وقالت عمْرَةُ الْخَثْعَمِيَّةُ ترثي ابنيها', 161, ' هُمَا أخْوَافي الْحَرْبِ مَنْ لاَ أخَالَهُ ', ' إذَا خافَ يَوْماً نَبْوَةً فَدَعاهُمَا )', 1),
('وقال آخر', 162, ' نِعْمَ الْفَتى زَعَمَ الرَّفِيقُ وَجارُهُ ', ' وَإذَا تَصَبْصَبَ آخِرُ الأَزْوَادِ )', 1),
('وقالت ريطةُ بِنتُ عاصم', 163, ' وَلوْ أنَّ سَلْمى نَالَها مِثْلُ رُزْئِنا ', ' لَهُدَّتْ ولَكنْ تَحْمِلُ الرُّزْءَ عَامِرُ )', 1),
('وقال امرأة من طيىء', 164, ' هوَ الأبْيَضُ الوضَّاحُ لوْ رُمِيَتْ بِهِ ', ' ضَوَاحِ مِنَ الرِّيَّانِ زَالتْ هِضابُها )', 1),
('وقال جَريرُ يرثي قَيس بن ضِرَار بن الْقَعقَاع بن مَعبَدٍ بن زُرَارةَ ', 165, ' غَيرَ أنَّ الْبأْسَ مِنهُ شِيمةٌ ', ' وصُرُوفُ الدَّهْر تَجْري بِالأجَلْ )', 1),
('وقال آخر يرثي ابنه', 166, ' فإنْ أبْكِ أبْكِ عَلى فَاجِعٍ ', ' وَإنْ يَكُ صَبرٌ فَمثْلِي صَبَرْ )', 1),
('وقال يحيى بن زياد تقدمت ترجمته', 167, ' وَلكِنْ إذَا مَا حَلَّ كُرْهٌ فَسامَحَتْ ', ' بِهِ النَّفْسُ يَوْمًا كانَ لِلْكُرْهِ أذْهَبا )', 1),
('وقال شبيب بن البرصاء المري ', 168, ' فَقَدْ جَعَلْتُ إذَا مَا حَاجَتِي نَزَلَتْ ', ' بِبابِ دَارِكَ أدْلُوهَا بأقْوَامِ )', 1),
('وقال مَعْنْ بن أوس', 169, ' تأوّبه طيف بذات الجرائم ', ' فنام رفيقاه وليس بنائم )', 1),
('وقال ربيعة بن مَقرُوم الضبي ', 170, ' إذَا زُرْتُ أرْضاً بَعْدَ طُولِ اجْتِنابهَا ', ' فَقَدْتُ صَدِيقِي والْبِلاَدُ كمَا هِيَا )', 1),
('وقال شبيب بن البرصاء المري تقدمت ترجمته', 171, ' إِذا المَرْءُ أعرَاهُ الصَّدِيقُ بَدَا لهُ ', ' بأرْض الأعادِي بَعْضُ ألْوَانِها الرُّبْدِ )', 1),
('وقال بعض الفزاريين', 172, ' وكُنْ أكْيَسَ اْلكَيْسَى إذَا كُنْتَ فِيهِمِ ', ' وإنْ كُنْتَ فِي الْحَمْقَى فَكُنْ أنْتَ أحْمَقَا )', 1),
('وقال آخر ', 173, ' عَسَى سَائلٌ ذُو حَاجَةٍ إنْ مَنَعْتَهُ ', ' مِنَ الْيَوْمِ سُؤْلاً أنْ يَكُونَ لهُ غَدُ )', 1),
('وقال مَنْظُورُ بنُ سُحَيم ', 174, ' فأنقذني منها حماري وجبتي ', ' جزى الله خيرا جبتي وحماريا )', 1),
('وقال آخر', 175, ' وَأُعرِضُ عَنْ مَطَاعِمَ قَدْ أرَاها ', ' فأترُكُها وَفي بَطْني انْطِواءُ )', 1),
('وقال آخر', 176, ' وإنّي لأنْسَى عِنْدَ كُلّ حَفِيظَةٍ ', ' إذا قِيلَ مَوْلاكَ احْتِمال الضَّغائنِ )', 1),
('وقال مالك بن حريم الهمدانيُّ ', 177, ' أُنْبئتُ وَالأَيَّامُ ذاتُ تجَاربٍ ', ' وتُبْدي لكَ الأَيَّامُ مَا لسْتَ تعْلمُ )', 1),
('وقال أيضاً والوزن كالأول', 178, ' لا تَيْأسَنَّ وَإنْ طَالَتْ مُطالَبَةٌ ', ' إذَا اسْتَعنْتَ بِصَبرٍ أنْ تَرَى فَرَجَا )', 1),
('قال المقَنَّع الكِنْدِيُّ ', 179, ' كأنها من جلود الباخلين بها ', ' عند النوائب تحذى بالمقاريض )', 1),
('وقال رجل من الفزاريين', 180, ' إذا كُنْتُ في الْقوْمِ الطِّوَالِ عَلوْتُهُمْ ', ' بِعارِفَةٍ حَتَّى يُقالَ طَوِيلُ )', 1),
('وقال مُضَرّسُ بنُ رِبْعيِّ ', 181, ' فإنك لا تعطي امرأ حق غيره ', ' ولا تعرف الشق الذي الغيث ماطره )', 1),
('قال أبو رياش هذه الأبيات للربيع بن أبي الحقيق اليهودي أما قيس بن الخطيم فقد تقدمت ترجمته وأما الربيع بن أبي الحقيق فإنه كان شاعرا من شعراء ', 182, ' وَلكنَّني لَمْ أنْسَ مَا قالَ صاحِبي ', ' نَصيبَكَ منْ ذُلٍّ إذَا كُنْتَ خالِيا )', 1),
('وقال يزيد بن الحكم الثقفي يعظ ابنه بدرا ', 183, ' ورثت جدي مجده وفعاله ', ' وورثت جدك أعنزا بالطائف )', 1),
('وقال مُنْقِدٌ الهلالي', 184, ' وَالْخَيْلُ أجْوَدُها الْمُناهِبُ ', ' عِنْدَ كَبَّتِها الأَزُومُ )', 1),
('وقال آخر', 185, ' تَجَلَّلْتَ عَاراً لاَ يَزَالُ يَشُبُّهُ ', ' سِبَابُ الرِّجالِ نَثرُهُمْ وَالْقصَائِدُ )', 1),
('وقال الْحَكَمْ بن عبدلٍ', 186, ' فَأفٍّ لدُنْيا لاَ يَدُومُ نَعِيمُها ', ' تَقَلَّبُ تَارَاتٍ بِنا وَتصَرَّفُ )', 1),
('وقال الفرزدق تقدمت ترجمته', 187, ' فقُلْ لِلشَّامِتينَ بِنا أفيقُوا ', ' سَيَلْقَى الشَّامِتونَ كمَا لَقِينَا )', 1),
('وقال حسان بن ثابت الأنصاري ', 188, ' كمَا الصَّمْتُ أدْنَى لِبَعْض الرَّشادِ ', ' فَبعْضُ التَّكلمَ أدْنَى لَغِي )', 1),
('قال الصّمة بن عبد الله بن طفيل بن الحرث بن قرَّةَ بن هبيرة ابن عامر بن سلمة الخير بن قشير بن كعب ', 189, ' أحْتَالُ لِلْمالِ أنْ أُودِي فأكْسِبُهُ ', ' ولَسْتُ لِلْعرْضِ أنْ أُودِي بِمُحتال ', 1),
('وقال آخر ', 190, ' وَأذْكُرُ أيَّامَ الْحِمَى ثُمَّ أنْثَنِي ', ' عَلى كَبِدِي مِنْ خَشْيَةٍ أنْ تَصَدَّعَا )', 1),
('وقال آخر ', 191, ' يوْمَ ارْتَحَلْتُ بِرَحْلي قبْلَ برْذَعَتي ', ' والْعَقْلُ مُتَّلةٌ والْقَلْبُ مَشْغُولُ )', 1),
('وقال الحسين بن مَطير الأسدي تقدمت ترجمته', 192, ' مُخَصَّرَةُ الأوْسَاطِ زَانَتْ عُقُودَها ', ' بأحْسَنَ ممَّا زَيَّنَتْها عُقُودُها )', 1),
('وقال أيضاً', 193, ' إنِّي أرَى وأظَنُّ أنْ سَتَرَى ', ' وَضَحَ النَّهارِ وَعالِيَ النَّجْمِ )', 1),
('وقال آخر', 194, ' أمَا وَالَّذِي حَجَّتْ لَهُ الْعِيسُ تُرْتَمى ', ' لِمَرْضاتِهِ شُعْثٌ طَويلٌ ذَميلُها )', 1),
('وقال آخر ', 195, ' وَمِمَّا شَجانِي أنَّها يوْمَ أعْرَضتَ ', ' تَوَلَّتْ وَماءُ الْعينِ في الْجَفْنِ حائِرُ )', 1),
('وقال ابن هَرْمةَ ', 196, ' قُلْتُ لَبَّيْكِ إذْ دَعانِي لَكِ الشَّوْقُ ', ' وَلِلْحَادِيَيْن حُثَّا الْمَطِيَّا )', 1),
('وقال آخر ', 197, ' ألاَ أيُّهَا الْقَلْبُ الَّذِي قادَهُ الْهَوَى ', ' أفِقْ لاَ أقَرَّ اللهُ عَيْنَكَ منْ قَلبِ )', 1),
('وقال عبد الله بن عجلان النهدي', 198, ' مراجِعُ نَجْدٍ بَعْدَ فِرْكٍ وَبِغْضَةٍ ', ' مُطَلِّقُ بُصْرَى أصْمَعُ القَلبِ جافِلُهْ )', 1),
('وقال عبد الله بن الدمينة الخثعمي تقدمت ترجمته ) ', 199, ' وَلمَّا لَحِقْنا بِالْحَمُولِ وَدُونَهَا ', ' خَمِيصُ الْحَشَا تُوهِي الْقَمِيص عَوَاتِقُهْ )', 1),
('قال أبو الطَّمَحان القيني', 200, ' وَقبْلَ غَدٍ يا لَهْفَ نَفْسي عَلى غَدٍ ', ' إذَا رَاحَ أصْحابِي ولَسْتُ بِرائِحِ )', 1),
('وقال شبْرُمَةُ بن الطفيل', 201, ' كَأَنَّ أبارِيقَ الشَّمُولِ عَشِيَّةً ', ' إوَزَّ بِأَعلَى الطَّفِّ عُوجُ الْحناجِرِ )', 1),
('وقال العَرندس ', 275, ' وَإنْ تَودَّدْتَهُمْ لانُوا وَإنْ شُهِمُوا ', ' كشَّفْتَ أذْمارَ شَرّ غيرَ أشْرارِ )', 1),
('وقال أبو الطمحان القيي واسمه حنظلة بن الشرقي تقدمت ترجمته', 276, ' إذَا قيلَ أيُّ النَّاسِ خَيْرٌ قبِيلَةً ', ' وأصبَرُ يوْماً لا تَوارَى كواكِبُهْ )', 1),
('وقال آخر ', 277, ' أجَلَّ جَلاَلةً وَأعَزَّ فَقْدًا ', ' وَأقضَى لِلْحُقُوقِ وَهُمْ قُعُودُ )', 1),
('وقال أبو دَهْبَلِ الجمحي يمدح النبي', 278, ' مُتَهَللٌ بِنَعَمْ بِلاَ مُتَبَاعِدٌ ', ' سِيّانِ مِنهُ الْوَفْرُ وَالعُدْمُ )', 1),
('وقالت أيضا ويقال بل قالها أبوها', 279, ' تبكي السُّيُوفُ إذا فَقَدْنَ أكُفَّنَا ', ' جَزَعًا وتَعْلَمُنا الزِّفَاقُ بُحُورَا )', 1),
('وقال آخر', 280, ' مثقفة صدورهما وشيفت ', ' صدور أسنة لهما حداد )', 1),
('وقال أيضًا', 281, ' بَعيدٌ منَ الشَّيءِ القَليلِ احْتِفاظُهُ ', ' عَلَيْكَ وَمنْزُورُ الرَّضِّا حِينَ يَغضَبُ )', 1),
('وقال أيضاً فيه', 282, ' حتَّى تَمَنىَّ البُرَاةُ أنَهُمُ ', ' عِنْدَك أمْسَوْا في القِدّ وَالْحَلَقِ )', 1),
('وقال آخر', 283, ' إذا انْتَدَى وَاحتَبَى بِالسَّيْفِ دَانَ لهُ ', ' شُوسُ الرِّجالِ خُضُوعَ الْجُرْبِ لِلطالِي )', 1),
('وقال عمرو بن الأطْنابَةِ أحدُ بَني الْخزْرَج ', 284, ' إنِّي مِنَ القَوْمِ الَّذينَ إذا انْتَدَوْا ', ' بَدْؤُا بحَقِّ اللهِ ثمَّ النَّائلُ )', 1),
('وقالت حبيبة بنت عبد العزى العوراء', 285, ' أُولِي عَلى هُلْكِ الطَّعامِ أليَّةً ', ' أبَداً وَلكنِّي أبِينُ وَأنْشُدُ )', 1),
('وقال عبد الله الحَواليّ منَ الأزد', 286, ' مُوَكَّلَةً بالأوَّلِينَ فَكلَّما ', ' رَأتْ رُفْقةً فالأوَّلُونَ لَها نُصْبُ )', 1),
('وقال آخرى', 287, ' نَصَبْنا لهُ جَوْفاءَ ذَاتَ ضَبابَةٍ ', ' منَ الدُّهْمِ مِبْطاناً طَويلاَ رُكُودُها )', 1),
('وقال آخر ', 288, ' ليس العطاء من الفضول سماحة ', ' حتى تجود وما لديك قليل )', 1),
('وقال عروة بن الورد تقدمت ترجمته', 289, ' إنِّي امرُوءٌ عافِي إناَئِيَ شِرْكةٌ ', ' وَأنْتَ امرُوءٌ عافِي إنائكَ وَاحدُ )', 1),
('قالَ المُثلَّمُ بنُ رياحٍ المُرِّي ', 290, ' وقُتودٍ ناجِيةٍ وضَعْتُ بِقفْرَةٍ ', ' والطَّيرُ غاشِيَةُ العَوَافي وُقَّعُ )', 1),
('وقال المساور بن هند بن قيس بن زهير تقدمت ترجمته', 291, ' فِدًا لِبني هِنْد غَدَاةَ دَعَوْتُهُمْ ', ' بِجَوَّ وبَالَ النَّفْسُ وَالأبَوَانِ )', 1),
('قال التبريزي هذه الأبيات لحاتم الطائي يخاطب امرأته ماوية بنت عبد الله ', 292, ' أيا ابْنةَ عَبْدِ اللهِ وَابنَةَ مالِكٍ ', ' ويا ابنةَ ذِي البُرْدَيْنِ والفَرَسِ الْوَرْد )', 1),
('وقال حزَازُ بنُ عمرٍو من بني عبد مناف', 293, ' ولَمْ تَكُ يَوْمًا إذَا رُوِّحَتْ ', ' عَلى الْحيِّ يُلْفَى لَها جادِبُ )', 1),
('وقال زيد الفوارس بن حصين بن ضررا تقدمت ترجمته', 294, ' أقِلي عليَّ اللَّوْمَ يَا ابنةَ مُنْذِرٍ ', ' وَنامِي فإنْ لَمْ تَشْتهِي النَّوْمَ فاسْهرِي )', 1),
('وقال الهُذَيلُ بن مَشجعةَ البُولانِيّ', 295, ' وَإذا أتَى مِنْ وِجهَةٍ بِطَريفَةٍ ', ' لمْ أطّلعْ مِمّا وَراءَ خِبائِهِ )', 1),
('وقالُ إيَاسُ بنُ الأَرَتّ', 296, ' وإنِّي لَقوَّالٌ لِعافيَّ مَرْحباً ', ' ولِلطَّالِبِ المَعْرُوفَ إنُّكَ واَجِدُهْ )', 1),
('وقال حسّان بن ثابت رضي الله عنه تقدمت ترجمته', 297, ' المالُ يَغْشَي رِجَالاً لاَ طباخَ بهِمْ ', ' كالسَّيْلِ يَغْشَى أُصُولَ الدِّنْدِنِ البَالي )', 1),
('وقال آخر ', 298, ' وَسِّعْ بمَدِّكَ ماءَ اللَّحْمِ تَقْسِمُهُ ', ' وَأكْثرِ الشَّوْبَ إنْ لمْ يَكثُرِ اللَّبَنُ )', 1),
('وقال حِماسُ بنُ ثامِل ', 299, ' ومسْتَنْبحٍ في لُجِّ لَيلٍ دَعَوْتُهُ ', ' بمَشْبُوبَةٍ في رَأسِ صَمْدٍ مُقابِلِ )', 1),
('وقال الفرزدق تقدمت ترجمته', 300, ' دَعا وَهْوَ يَرْجُو أنْ يُنَبِّهَ إذ دَعا ', ' فتًى كابْن لَيلى حينَ غارَتْ نُجُومُها )', 1),
('قال شرَيّحُ بنُ الأحوَصِ بن جعفر بن كلاب ', 301, ' فَاتَ وإنْ أسرَى منَ اللَّيلِ عُقْبةً ', ' بِلَيْلةِ صِدْقٍ غابَ عَنْها شُرُ برُها )', 1),
('وقال جابر بن حيان', 302, ' وَما وجَدَ الأضْيافُ فِيما يَنْوبُهُمْ ', ' لهُمْ عِنْدَ عِلاَّتِ الزَّمانِ أباً مِثْلي )', 1),
('وقال أيضاً', 303, ' وَإنِّي لأَسْتَحيِي يمِيني وَبيْنَها ', ' وبَينَ فَمي دَاجى الظَّلاَمِ بَهيمُ )', 1),
('وقال عُتبةُ بنُ بُجير وقيل أنها لمسكين الدراميّ', 304, ' أُحِدِّثُه إنَّ الْحَدِيثَ منَ القِرى ', ' وتَعْلَمُ نَفْسِي أنَّه سوْفَ يَهْجَعُ )', 1),
('وقال المَرَّار الفَقْغسي تقدمت ترجمته', 305, ' إذا قالَ مَنْ أنْتُمْ لِيَعرَفَ أهْلَها ', ' رَفَعْتُ لهُ باسْمِي ولَمْ أتَنكَّرِ )', 1),
('وقالَ الأقرعُ بنُ مُعاذ', 306, ' تُسلِّفُ الْجَارَ شِرْباً وهْيَ حائمَةٌ ', ' ولاَ يَبِيتُ عَلى أعْناقِها قَسَمُ )', 1),
('وَقال سَوَادةُ اليربُوعيُّ', 307, ' لاَ أحْبِسُ المَالَ إلاّ رَيْثَ أُتْلِفُهُ ', ' وَلا تُغيِّرُني حالٌ إلى حَالِ )', 1),
('وقال المقَّنع الكندِيُّ تقدمت ترجمته', 308, ' لَيسَ العَطاءُ منَ الفَضُولِ سَماحَةً ', ' حتَّى تَجُودَ وَما لدَيْكَ قلِيلُ )', 1),
('وقال عبدُ الله بنُ الْحَشْرَج الجعدي ', 309, ' وتَربِيَتي الصَّغيرَ إِلى مَدَاهُ ', ' وتَأمِيلي هِلاَلاً عَنْ هَلاَلِ )', 1),
('وقال عارِق الطائيُّ تقدمت ترجمته', 310, ' ألا حيِّ قبْلَ البيْنِ منْ أنت عاشِقُهْ ', ' ومنْ أنت مُشْتاقٌ إليه وشائقُهْ )', 1),
('وقال مِلحة الجرمي', 311, ' إذا مَا رَمى أصْحابُهُ بجَبينه ', ' سُرَى اللَّيْلةِ الظَّلْماءِ لمْ يَتهكَّم )', 1),
('وقال يزيد الحارثي', 312, ' وإذا الفَتى لاقَى الْحِمامَ رَأيْتهُ ', ' لوْلاَ الثَّناءُ كأنَّهُ لَمْ يُولَدِ )', 1),
('وقال أبو تمام لما أتى يزيد بن عبد الملك بآل المهلب قام كثير بين يدي يزيد فقال ', 313, ' أساؤا فإنْ تَغْفرْ فإِنَّكَ أهلُهُ ', ' وأفْضَلُ حِلمٍ حِسْبةً حِلْمُ مُغضَبِ )', 1),
('قال ابن المولى ليزيد بن حاتم بن قبيصة بن المهلب ', 314, ' وإذا صَنعْتَ صَنِيعةً أتْمَمتَها ', ' بيَدَيْنِ لِيسَ نَداهُما بِمُكَدَّرِ )', 1),
('قال خلف بن خليفة مولى قيس بن ثعلبة ', 315, ' هو اللص وابن اللص لا لص مثله ', ' لنقب البيوت أو لطر الدراهم )', 1),
('وقال طُريْحُ بن إسماعيلَ الثّقفيّ ', 316, ' فأرْجِعُ مَغبُوطاً وَترْجِعُ بالّتي ', ' لَها أوَّلٌ في المَكرُمَاتِ وآخِرُ )', 1),
('وقال أعشى بني أبي رَبيعة ', 317, ' وما أنا في أمري ولا في خصومتي ', ' ) الأبيات فلما فرغ قال عبد الملك من يلومني على هذا وأمر له بصلة كبيرة', 1),
('وقال نصيب في عمر بن عبيد الله بن معمر التيمي تقدمت ترجمته', 318, ' أيَوْمٌ إذا ألْفَيْتَهُ ذا يَسارَةٍ ', ' فأعْطَيْتَ عَفْواً مِنكَ أمْ يَوْمُ تُجْهَدُ )', 1),
('وقال ابن عبدل الأسدي تقدمت ترجمته', 319, ' فإِذا ابْنُ بِشْرٍ في مَواكِبِه ', ' تَهْوِي بِهِ خَطَّارَةٌ سُرُحُ )', 1),
('وقالت قتيلة أخت النضر بن الحرث تقدمت ترجمتها', 320, ' الوَاهِبُ الألْفَ لاَ يبْغِي بهَا بدَلاً ', ' إلاّ الإِلهَ وَمعْرُوفاً بمَا اصْطنَعا )', 1),
('وقالت أخرى', 321, ' منْ كُلِّ مَحْبوكٍ طُوالِ القَرَى ', ' مِثْل سِنانِ الرُّمْحِ مَشْهومِ )', 1),
('وقالت امرأة من إياد', 322, ' لاَ يرْهَبُ الجَارُ مِنهُ غَدْرَةً أبداً ', ' وإنْ ألمَّتْ أُمورٌ فهْوَ كافيهَا )', 1),
('وقال عَنترَةُ بنُ الأَخرَسِ', 323, ' كأنَّ بِضاحِي جِلْدِهِ وسَراتِهِ ', ' ومَجْمَعِ لِيتَيْهِ تَهاويلَ زُخْرُفِ )', 1),
('وقال آخر', 324, ' دَعَوْتُ فتًى أجَاب فتًى دَعاهُ ', ' بِلَبَّيْهِ أشَمَّ شَمَرْدَليِّ )', 1),
('وقال آخر', 325, ' نَكادُ إذا قُمْنا يُطِيرُ قُلوبَنا ', ' تَسَرْبُلُنا وَلوْثُنا بالعَصائبِ )', 1),
('وقال حكيمُ بنُ قبيصَةَ الضبيّ ) ', 326, ' فَما جَنةَ الفِرْدَوْسِ هَاجَرْتَ تَبْتَغي ', ' ولكنْ دَعاكَ الْخُبْزُ أحسَبُ والتّمرُ )', 1),
('وقال حُندُجُ بنُ حُندُجِ المُرِّيّ', 327, ' لِساهِر طالَ فِي صُولٍ تَمَلْمُلُهُ ', ' كأَنّهُ حيَّةٌ بِالسَّوطِ مَقْتولُ )', 1),
('قالية مبغضة والمعنى أن نساء الرجال الطاعنين في السن في غم وكرب يتمنين مفارقتهم ويبغضن مصاحبتهم لما يجدنه من نكد العيش وضيقه ', 328, ' نَكَحْتُ المَدِينِيَّ إذْ جَاءَني ', ' فيَالَكِ منْ نَكحةٍ غاليَهْ )', 1),
('وقال آخر ومر بأبي العلاء العقيلي يفلي ثيابه', 329, ' وكأنَّهُنَّ لدَى دُرُوزِ قَمِيصهِ ', ' فذٌّ وَتوْأمُ سِمْسِمٍ مَقْشورِ )', 1),
('وقال آخر وقد سُرِقَتْ له دَلوٌ ', 330, ' أفيضُوا عَلى عُزَّابِكُمْ بنِسائِكُمْ ', ' فَما فِي كتابِ اللهِ أنْ يَحْرُمَ الفَضلُ )', 1),
('وقال آخر ', 331, ' من يشتري مني زوجا خبا ', ' أخب من ضب يداهي ضبا )', 1),
('وقال آخر ', 332, ' أنِخْ فَاصْطَبِغْ قرْصاً إذا اعْتادَكَ الهَوَى ', ' بِزَيْتٍ كمَا يكْفيكَ فقْدَ الْحَبائِبِ )', 1),
('وقال آخر ', 333, ' وقال بِلالَ بنُ جرير )', ' حتى أقُولَ لعَلَّ الضَّيْفَ قَدْ وَلدَا )', 1),
('وقال أعرابي لابنه وكان قد دخل الحمام فأحرقته النورة', 334, ' أجِدَّكُما لَمْ تَعْلَما أنّ جَارَنا ', ' أبا الحِسلِ بالصّحْرَاءِ لاَ يَتَنوَّرُ )', 1),
('وقالت أخرى ', 335, ' [لَعَمرِي]لَقدْ أخْلَفتَ ظَنِّي وسُؤتَنِي ', ' فَحُزْتَ بِعصْيانِي النَّدَامةَ فَاصْبِر )', 1),
('وقال سعد ابنها وليس من الكتاب', 336, ' ليْستْ بشَبْعَى وَلو أوْرَدْتَها هجَراً ', ' ولا بِرَيّا ولوْ قاظَتْ بذِي قارِ )', 1),
('قال بعضهم ', 337, ' فإن أنفلت من عمر صعبة سالما ', ' تكن من نساء الناس في بيضة العقر )', 1),
('وقال آخر ', 338, ' تَمَّتْ عُبَيْدَةُ إلاَّ مِنْ مَحَاسِنِها ', ' والْمِلْحُ منْها مكانَ الشَّمْس والْقَمرِ )', 1),
('وقال آخر ', 339, ' رَقْطاءُ حَدْباءُ يُبْدِي الكِبْدَ مضْحكُها ', ' قنوْاءُ بالْعَرْض والعَينَانِ بالطُولِ )', 1),
('وقال آخر', 340, ' وإنْ حَدَّثَتْ كانَتْ جميعَ مَصائِبٍ ', ' مُوَفَّرَةٍ تأتِي بِقاصِمَةِ الظهرِ )', 1),
('وقال آخر ', 341, ' عَلى نَغانِغَ سَالتْ في بَلاعِمِهَا ', ' كَثِيرَةِ الوَشْيِ في لِينٍ وتَرْقِيق )', 1),
('onezerotwo', 1012, 'zerotwo', 'two', 102),
('onezerothree', 1013, 'zerothree', 'three', 103),
('قال قُرَيطُ بنُ أُنَيف أحد بني العنبر', 1, '', '', 1),
('قال الْفِنْدُ الزِّمَّاني في حرب البَسُوس', 2, ' فَلَيْتَ لِي بِهِمِ قَوْمًا إذَا رَكِبُوا ', ' شَدُّوا الإِغَارَةَ فُرْسَانًا وَرُكْبانَا )', 1),
('وقال أبو الغول الطَّهَويّ', 3, ' فَدَتْ نَفْسِ وَمَا مَلَكَتْ يَمِينِي ', ' فَوَارِسَ صَدَّقتْ فِيهِمْ ظُنُونِي )', 1),
('وقال جعفرُ بنُ عُلبةَ الحارثيّ', 4, ' فَقالُوا لنَا ثِنْتَانِ لاَ بُدَّ مِنْهُما ', ' صُدُورُ رِماحٍ اشُرِعَتْ أوْ سَلاَسِلُ )', 1),
('وقال أيضاً', 5, ' فَلاَ تَحسبي أنِّي تَخَشَّعْتُ بَعْدَكُمْ ', ' لِشَيْءٍ وَلاَ أنِّي مِنَ الْمَوْتِ أفْرقُ )', 1),
('قال ربيعة بن مَقروم الضبيّ ', 6, ' بِضَرْبَةٍ لَمْ تَكُنْ مِنِّي مُخَالِسَةً ', ' ولاَ تَعَجَّلْتُها جُبْناً ولاَ فَرَقَا )', 1),
('قال أبو كبير الْهُذَلِي ', 7, ' ممَّنْ حَمَلْنَ بِهِ وَهُنَّ عَوَاقِدٌ ', ' حُبُكَ النِّطَاقِ فَشَبَّ غَيْرَ مُهَبَّلِ )', 1),
('وقال تأبط شرًّا أيضاً', 8, ' يَحْمي الصِّحَابَ إِذَا تَكُونَ عَظِيمَةٌ ', ' وَإِذَا هُمُ نَزَلُوا فَمَأْوَى الْعُيَّلِ )', 1),
('وقال بعض بني قيس بن ثعلبة ', 9, ' إنَّا بني نَهْشَلٍ لا نَدَّعي لأَبٍ ', ' عَنْهُ وَلاَ هُوَ بِالأبْناءِ بَشْرِينَا )', 1),
('قال السَّمَوْأل بن عادِياءَ ', 10, ' وَنَرْكَبُ الكُرْهَ أحْياناً فَيَفْرِجُهُ ', ' عَنَّا الْحِفاظُ وَأسْيافٌ تُواتِينَا )', 1),
('قال الشَّميْذَرُ الحارثيُّ ', 11, ' بَنِي عَمِّنا لاَ تَذْكُروا الشِّعْرَ بَعْدَما ', ' دَفَنْتُمْ بِصَحْراءِ الْغُميْرِ الْقَوافِيا )', 1),
('وقال سَوَّارُ بنُ الْمُضَرَّب السَّعْدِيّ ', 12, ' إذَا اسْتُنْجِدُوا لَمْ يَسألُوا مَنْ دَعَاهُمُ ', ' لأَيَّةِ حَرْبٍ أمْ بِأَيِّ مَكانِ )', 1),
('وقال قَطَرِيُّ بن الْفُجاءَةِ المازِنيّ', 13, ' حَتَّى خَضَبْتُ بِما تَحَدَّرَ مِنْ دَمِي ', ' أَكْنافَ سَرْجِي أوْ عِنَانَ لِجَامِي )', 1),
('قال المبرد النثرة الدرع السابغة يقول درعي هذه تكفيني وقوله كل ', 14, ' وَالدِّرِْعُ لاَ ابْغِي بِهَا نَثْرَةً ', ' كلُّ امْرِئٍ مُسْتَوْدَعٌ مَالَه )', 1),
('وقال الأشْتَرُ النَّخَعِيُّ ', 15, ' أنَا ابنُ زَيَّابَةَ إنْ تَدْعُنِي ', ' آتكَ والظَّنُّ عَلَى الْكاذِبِ )', 1),
('وقال زُفَرُ بنُ الحَرِثِ ', 16, ' وَكُنَّا حَسِبْنا كُلَّ بَيْضاءَ شَحْمَةً ', ' لَيالِيَ لاَقَيْنَا جُذَامَ وَحِمْيَرَا )', 1),
('وقال عَمْرُو بْنُ مَعْدِ يكرِب الزُّبَيْدِيُّ ', 17, ' أقدم فيهم دعلجا وأكره ', ' إذا أكرهوا فيه الرماح تحمحما )', 1),
('وقال سيَّارُ بن قَصِيرٍ الطّائيّ ', 18, ' لَوْ شَهِدَتْ أمُّ الْقُدَيْدِ طِعَانَنَا ', ' بِمَرْعَشَ خَيْلَ الأَرْمَنِيِّ أرَنَّتِ )', 1),
('وقال أَنَيْفُ بنُ زَبّان النَّبْهانيُّ مِنْ طَيىء ', 19, ' إنْ تُذْنِبُوا ثُمَّ تَأتِيني بَقِيَّتُكُمْ ', ' فَمَا عَلَيَّ بِذَنْبٍ عِنْدَكُمْ فَوْتُ )', 1),
('وقال قيس بن الخَطيم ', 20, ' وَابْنُ صُبْحٍ سَادِراً يُوْعِدُنِي ', ' مَالَهُ فِي النَّاسِ مَا عِشْتُ مُجِيرُ )', 1),
('وقال الفَرَّارُ السَّلَميُّ ', 21, ' فَتَرَكْتُهُمْ تَقِصُ الرِّمَاحُ ظُهُورَهُمْ ', ' مِنْ بَيْنِ مُنْعَفِرٍ وَآخَرَ مُسْنَدِ )', 1),
('وقال الشُّدَاخُ بنُ يَعْمُرَ الْكِنَانِيّ ', 22, ' ذَكَرْت تَعِلَّةَ الْفِتْيَانِ يَوْماً ', ' وَالْحَلقَ الْمَلاَمَةِ بالْمُلِيم )', 1),
('وقال رجل مِنْ بني عُقَيْل', 23, ' لَهَا لَوْنٌ مِنَ الهَامَاتِ كَابٍ ', ' وَإنْ كَانَتْ تُحَادَثُ بِالصِّقَالِ )', 1),
('وقال قيس بن زهير بنَ جذِيمة العبْسيّ فِي قَتْلِهِ حَمَلَ بن بدر يومَ جفر الهبَاءَة ', 24, ' وَلَمَّا رَأيْتُ أنَّنِي قَدْ قَتَلْتُهُ ', ' نَدِمْتُ عَلَيْهِ أيَّ سَاعَةِ مَندَمِ )', 1),
('وقال رجل من بني تميم ', 25, ' سَليلَةُ سَابِقَيْنِ تَنَاجَلاَهَا ', ' إذَا نُسِبَا يَضُمُّهُما الْكُرَاعُ )', 1),
('وقال بعض بني فَقْعَس ', 26, ' فَهَلاَّ أَعَدُّونِي لِمِثْلِي تَفَاقَدُوا ', ' إذَا الخَصْمُ أبْزَى مَأئِلُ الرَّأسِ أنْكَبُ )', 1),
('قالت كبشة أخت عمرو بن معد يكرب ', 27, ' ودَعْ عَنْكَ عمْراً إنَّ عَمْرًا مُسَالِمٌ ', ' وَهَل بَطْنُ عَمْرٍ وغَيْرُ شِبْرٍ لِمَطْعَمِ )', 1),
('وقال الأحوص بن محمد بن عاصم بن ثابت بن أبي الأفلح الأنصاري ', 28, ' إذَا أبْصَرْتَنِي أعْرَضْتَ عَني ', ' كَأنَّ الشَّمْسَ مِنْ قِبَلِي تَدُورُ )', 1),
('وقال الطِّرِمّاح بْن حَكيم ', 29, ' كُلٌّ لَهُ نِيَّةٌ فِي بُغْضِ صَاحِبِه ', ' بِنِعْمةِ اللهِ نَقْليكُمْ وَتَقْلُونَا )', 1),
('وقال بعض بني فقعس ', 30, ' نَاسَيْتُهمْ بَغْضاءَهُمْ وتَرَكْتُهمْ ', ' وَهُمُ إذَا ذَكِرَ الصَّدِيقُ أعادِي )', 1),
('وقال جابر بن رَأْلاَن السَّنْبِسِيّ ', 31, ' فإِن تُبْغِضُونَا بِغضَةً فِي صُدُورِكُمْ ', ' فإِنَّا جَدَعتَا مِنْكمُ وشَرَينا )', 1),
('وقال آخر من بني فقعس ', 32, ' فإِنْ تَغْمِزْ مَفَاصِلَنا تَجِدْهَا ', ' غِلاَظاً فِي أنَامِلِ مَنْ يَصولُ )', 1),
('وقال مِسوَرُ بن زيادةَ الْحارثي', 33, ' أبَعْدَ الَّذِي بِالْنَّعْفِ نَعْفِ كُوَيْكِبٍ ', ' رَهِينَةِ رَمْسٍ ذِي تُرَابٍ وَجنْدَلِ )', 1),
('وقال آخر ', 34, ' اللُّؤْمُ أكْرَمُ منْ وَبْرٍ وَوالِدِهِ ', ' وَاللُّؤْمُ أكْرَمُ مِنْ وَبْرٍ ومَا ولَدَا )', 1),
('وقال حُرَيثُ بنُ عنَّاب النبهانِيّ ', 35, ' تَعَالَوْا أفَاخِرْكُمْ أَأَعْيا وَفَقْعَسٌ ', ' إلَى الْمَجْدِ أَدْنَى أَمْ عَشِيرَةُ حَاتِمِ )', 1),
('وقال آخر', 36, ' وكمْ دَهِمتْنِي مِنْ خُطُوبٍ مُلِمَّةٍ ', ' صَبَرْتُ عَليْها ثُمَّ لَمْ أتَخَشَّعِ )', 1),
('وقال بعض بني عبد شمس بن فقعس', 37, ' لاَذَتْ هُنَالِكَ بِالأشْعافِ عالِمَةً ', ' أنْ قدْ أطاعَتْ بِلَيْلٍ أمْرَ غاوِيها )', 1),
('وقال آخر ', 38, ' وَفارَقْتُ حتى مَا أُبَالِي مِنَ النَّوَى ', ' وَإنْ بَانَ جِيرَانٌ عَليَّ كِرَامُ )', 1),
('وقال آخر', 39, ' أقلب عيني لا أرى من أحبه ', ' وفي الدار ممن لا أحب كثير )', 1),
('وقال عمرو بن شأْس ', 40, ' فَإنْ كُنْتِ مِنِّي أوْ تُرِيدِينَ صُحْبَتِي ', ' فَكُونِي لَهُ كَالسَّمْنِ رُبَّتْ لهُ الأَدَم )', 1),
('وقال حِطَّانُ بنُ الْمُعَلَّى ', 41, ' أنْزَلَنِي الدَّهْرُ عَلى حُكْمِهِ ', ' مِنْ شَامِخٍ عَالٍ إلى خَفْضِ )', 1),
('قال أبو هلال هكذا قال أبو تمام ونحن نقول هو حيان بن عليق بن ربيعة الطائي أخو بني أخزم ينتهي نسبه إلى عمرو بن ثعل وهو شاعر جاهلي ', 42, ' أنا أبُو بَرْزَةَ إذْ جَدَّ الْوَهَلْ ', ' خُلِقْتُ غَيْرَ زُمَّلٍ وَلاَ وَكَلْ )', 1),
('وقال رجل من بني كلب', 43, ' فَإِنِّي مِثْلُ مَا تَجِدِينَ وَجْدِي ', ' وَلكنْ أصْبَحَتْ عَنْهُمْ قَرُونِي )', 1),
('قال أبو حنْبل الطائي ', 44, ' قَدْ كانَ سَيرٌ فَحُلَّوا عَنْ حَمُولَتِكُمْ ', ' إنِّي لِكُلّ امْرِئٍ مِنْ جَارهِ جَارُ )', 1),
('وقال جابر بن الثعلب الطائي', 45, ' وَمنْ يَفْتقرْ في قَوْمِهِ يَحْمَدِ الْغَنى ', ' وإنْ كانَ فِيهمْ وَاسِطَ الْعَمّ مُخْوِلا )', 1),
('وقال الراعي', 46, ' فَباتَ يُرِيهِ عِرْسَهُ وَبَناتِهِ ', ' وبِتُّ أُردِيهِ النَّجْمَ أيْنَ مَخافِقُهْ )', 1),
('وقال أبو النَّشْناش ', 47, ' لِيُكْسِبَ مَجْداً أوْ لِيُدْرِكَ مَغْنماً ', ' جَزِيلاً وَهَذَا الدَّهْرُ جَمٌّ عَجاَئِبُهْ )', 1),
('وقال آخر ', 48, ' فإِمَّا تَرَيني الْيَوْمَ أصْبَحتُ بَادِناً ', ' لَدَيْكِ فَقدْ أُلْفَيَ عَلى الْبُزْلِ مِرْجَمَا )', 1),
('قال أبو رياش هذا غلط من أبي تمام لأن يحيى ابن منصور ذهلي وإنما هذه الأبيات لموسى بن جابر الحنفي وكلاهما شاعر إسلامي مجيد وسيأتي لموسى بن جابر ذكر ', 49, ' وَجَدْنا أبانا كانَ حَلّ بِبَلْدَةٍ ', ' سِوًى بَيْنَ قَيْسٍ قَيْسِ عَيْلاَنَ وَالفِزْرِ )', 1),
('وقال بعض بني عبس', 50, ' وأخْلاَقَنا إعْطَاءَنَا وإبَاءَنَا ', ' إذا مَا أبَيْنا لاَ نَدُرُّ لَعَاصِبِ )', 1),
('وقال حسان بن نشْبَةَ العدوي في ذلك ', 51, ' وكَمْ تَركْنا هُناكَ مِنْ بَطَلٍ ', ' تَسْفِي عَلَيْهِ الرِّيَاحُ فِي لِمَمِهْ )', 1),
('وقال في ذلك هلال بن رزين أحد بني ثور بن عبد مناة بن أُدّ', 52, ' وَبالبَيْدَاءِ لمَّا أنْ تَلاَقَتْ ', ' بِها كَلْبٌ وَحَلَّ بها النُّذُورُ )', 1),
('قال جَزْءُ بن ضِرار أخو الشماخ ', 53, ' فإِنْ يَكُ حَقًّا مَا أتَاني فإِنَّهُمْ ', ' كرَامٌ إذَا مَا النَّائِباتُ تَنُوبُ )', 1),
('قال حُجْر بن خالد بن محمود بن عمرو بن مَرْثِدِ بن مالك بن ضُبَيْعَةَ بن قيس بن ثعلبة ', 54, ' وإذَا هلكْتُ فَلاَ تُريدِي عاجِزاً ', ' غُسًا وَلا بَرَماً ولاَ مِعْزَالاَ )', 1),
('وقال جعفر بن عُلبَة الحارثيّ حين لقي بني عُقَيْل وقد تقدمت ترجمته ', 55, ' مَن يَلْقِني يُودِ كمَا أَوْدَتْ إرَم ', ' )', 1),
('وقال البُرْج بن مُسْهر الطائي ', 56, ' إذا كنت في قوم عدا لست منهم ', ' ) أي وأنت لا تهوى هواهم فكل مما علفت وهذا من الأمثال', 1),
('وقال آخر من بني أسد في يوم اليمامة', 57, ' أقُولُ لِنَفْسي حِينَ خَوَّدَ رأْلُها ', ' مَكانَكِ لَمَّا تُشْفِقِيِ حينَ مُشْفَق )', 1),
('وقال أيضاً', 58, ' ألَمْ ترَياَ أنِّي حَمَيْتُ حَقيقَتيِ ', ' وَباشَرْتُ حَدَّ الْمَوْتِ وَالْمَوْتْ دُونُهاَ )', 1),
('وقال البَعيثُ بن حُرَيْث ', 59, ' فَقُلْتُ لهُ أهْلاً وسَهْلاً وَمَرْحَباً ', ' فَرَدَّتْ بِتَأْهيلٍ وَسَهْلٍ وَمَرْحَبِ )', 1),
('وقال المُثَّلم بن رياح بن ظالم المُرّى ', 60, ' مَنْ مُبْلِغٌ عَنّى سِنَاناً رِسالةً ', ' وِشِجْنةَ أنْ قُوما خُذَا الْحَقَّ أوْدَعا )', 1),
('قال حصين بن حمام المرّي ', 61, ' لعمرك ما ضلت ضلال ابن جوشن ', ' حصاة بليل ألقيت وسط جندل )', 1),
('وقال بَشَامة بن حَزْن ', 62, ' إِنِّي امْرُؤٌ اسِمُ الْقَصائِدَ لِلْعِداَ ', ' إنَّ الْقصائِدَ شَرُّها أغْفَالُهَا )', 1),
('وقال عَقيل بن عُلَّفَةَ المرّي ', 63, ' تَنَاهَوْا وَاسْأَلُوا ابْنَ أَبِي لَبيدٍ ', ' أَأَعْتَبَهُ الضُّبارِمَةُ النَّجيدُ )', 1),
('وقال آخر', 64, ' فَدَامَ لِي وَلَهُمْ ما بِي وَما بِهِمِ ', ' وَماتَ أكْثرُنا غَيْظاً بِما يَجِدُ )', 1),
('وقال طَرَفةُ الخُزيميُّ ', 65, ' وَما غَمَراتُ المَوْتِ إلاّ نِزَالُكَ الْكَمَّى ', ' عل لَحمِ الْكَمِيِّ المقَطَّر )', 1),
('وقال أيضا', 66, ' وَإنَّ نِجَاري يَا ابْنَ غَنْمٍ مُخَالِفٌ ', ' نِجارَ اللِّئَامِ فَابْغنِي منْ وَرائِيا )', 1),
('وقال عُروة بن الوَرْد ', 67, ' لَحَا اللهُ صُعْلُوكاً إذَا جَنَّ لَيْلُهُ ', ' مُصَافِي المُشَاشِ آلِفاً كلَّ مَجْزَر )', 1),
('وقال عنترة تقدمت ترجمته', 68, ' تَرَكْتُ بَني الْهُجَيْمِ لَهُمْ دَوَارٌ ', ' إذَا تمْضِي جَمَاعَتُهُمْ تعود )', 1),
('وقال أيضاً', 69, ' أتَشْحَذُ أرْماحاً بِأَيْدِي عَدُوِّنا ', ' وتَتْرُكُ أرْحاماً يِهنَّ تُكَابِدُ )', 1),
('قال أبو الفتح الشارق اسم صنم لهم ولذلك قالوا عبد الشارق كما قالوا عبد العزى والعزى صنم أيضا ومثل ذلك عبد يغوث وعبد ود ونحوه ', 70, ' ألا حُيِّيِتِ عَنَّا رُدَيْنا ', ' نُحيَيّيهاَ وَإِنْ كَرمَتْ عَلَيْنا )', 1),
('قال بِشْرُ بنُ أبَيّ بن حمام العبسي لبني زهير بن جذيمة ', 71, ' لُطِمْنَ عَلى ذَاتِ الإِصادِ وجَمْعُكُمْ ', ' يَروْنَ الأَذَى منْ ذِلَّةٍ وَهوانِ )', 1),
('وقال المساور بن هند بن زهير', 72, ' وَأرَى الْغَوَانِيَ بَعْدَماَ أوْجَهْنَني ', ' أعْرَضْنَ ثُمَّتَ قُلْنَ شَيْخٌ أعْوَرُ )', 1),
('وقال عُرْوَةُ بن الوَرْدِ الْعَبْسِي ', 73, ' وَلَنا قَنَاةٌ مِنْ رُدَيْنَةَ صَدْقَةٌ ', ' زَوْرَاءُ حَامِلُهَا كَذَلِكَ أزْوَرُ )', 1),
('قال هُدْبة بن خَشرَم ', 74, ' إنّي مِنْ قُضَاعة مَنْ يَكِدْها ', ' أكِدْهُ وَهْيَ مِنِّي في أمان )', 1),
('وقال عبد الله بن سبرة الحرَشي ', 75, ' إنِّي امْرُؤٌ مِنْ تَنُوخَ ناصِرُهُ ', ' مُحْتَمِلٌ فِي الْحُرُوبِ مَا احْتَمَلُوا )', 1),
('وقال الشَّنْفَرى الأزدي', 76, ' إذا نَفَرَتْ مِنْ بَيَاضِ السُّيُوفِ ', ' قُلْنا لَها أقْدِمي مُقْدَمَا ', 1),
('قال تأبط شرًّا ', 77, ' قَلِيلُ غِرَارِ النَّوْمِ أكْبَرُ هَمِّهِ ', ' دَمُ الثَّأْرِ أوْ يَلْقَى كَمِيًّا مُسَفّعا )', 1),
('وقال بعض بني قيس بن ثعلبة', 78, ' إذا ما قُلُوبُ الْقَوْمِ طارَتْ مَخَافَةً ', ' مِنَ الْمَوْت أرْسَوْا بِالنفُوسِ الْمَوَاجِدِ )', 1),
('قال جَحْدَرُ بنُ ضُبَيْعَةَ بن قيس بن ثعلبة ', 79, ' قَدْ عَمِلَتْ وَالِدَةٌ مَا ضَمَّتِ ', ' مَا لَفَّفَتْ فِي خِرَق وَشَمَّتِ )', 1),
('وقال حجر بن خالد الثعلبي ', 80, ' وجَدْنا أبَانا حَلَّ فِي الْمَجْدِ بَيْتُهُ ', ' وأَعْيا رِجَالاً آخَرِينَ مَطالِعُهُ )', 1),
('وقال حجر بن خالد أيضا', 81, ' غَدَاةَ أتَاهُ جَبَّارٌ بِإِدٍّ ', ' مُعَضِّلةٍ وَحَادَ عَنْ الْقتَالِ )', 1),
('وقال بعض بني جُهَيْنَة في وقعة كلب وفَزارة ', 82, ' فَإِنَّ ابْنَ أخْتِ الْقَوْمِ مُصْغًى إنَاؤُهُ ', ' إذَا لَمْ يُزَاحِمْ خَالهُ بِأَبٍ جَلْدِ )', 1),
('وقال سُلْميُّ بنُ رَبيعة من بني السِّيد بن ضَبَّةَ ', 83, ' كَأنَّ عَلَى سَوَاعِدِهِنَّ وَرْساً ', ' عَلاَ لَوْنَ الأشَاجِعِ أوْ خِضَابَا )', 1),
('وقال أيضاً', 84, ' فِدى لِفَتًى ألْقَى إلَيَّ بِرَأْسِها ', ' تِلاَدِي وَأهْلي مِنْ صَدِيقٍ وَجَامِل )', 1),
('وقال حُسَيْل بن سُجَيْح الضبي ', 85, ' وأرْهَبْتُ أُولَى الْقَوْمِ حَتَّى تَنَهْنَهُوا ', ' كَما ذُدْتَ يَوْمَ الْوِرْدِ هِيماً خَوَامِسا )', 1),
('وقال مُحْرزَ بنُ المكعْبر الضَّبيّ ', 86, ' حَتَّى أتى عَلمَ الدَّهْنا يُوَاعِسُهُ ', ' وَاللهُ أعْلمُ بالصَّمَّانِ مَا جَشِمُوا )', 1),
('وقال أبو ثمامة بن عازب الضبي ', 87, ' أُخَاصِمُهُمْ مَرَّةً قَائِمًا ', ' وأجْثُوا إذَا مَا جَثَوْا لِلرُّكَبْ )', 1),
('وقال عبد الله بن عَنَمَةَ الضبي ', 88, ' تحية بينهم ضرب وجيع ', ' ) فالضرب لا يكون تحية', 1),
('وقال الفضل بن الأخضر بن هبيرة الضبي', 89, ' ولاَ تَكُونَنْ كَمُجْرَى دَاحِسٍ لَكُمُ ', ' فِي غَطَفانَ غَدَاةَ الشِّعْبِ عُرْقُوبُ )', 1),
('وقال إياسُ بنُ مالكِ بن عبدِ اللهِ بن خَيْبَرَى الطائي', 90, ' إذْ لا تَخَافُ حُدُوجُنَا قَذَفَ النَّوَى ', ' قَبْلَ الْفَسادِ إقامَةً وَتَدَيُّرَا )', 1),
('وقال عبد الرحمن المَعْنيُّ ', 91, ' ثمانُونَ ألْفاً وَلَمْ أُحْصِهِمْ ', ' وَقَدْ بَلَغَتْ رَجْمَها أوْ تَزِيدُ )', 1),
('وقال جابر بن رألان السّنبسي', 92, ' تَجَوَّدْتُ فِي مَجْلِسٍ وَاحِدٍ ', ' قِرَاها وَتِسْعينَ أمْثَالَها )', 1),
('قال قبيصة بن النصراني الجرمي من طيىء ', 93, ' عَشِيَّةَ قَطَّعْنا قَرَائِنَ بَيْنَنا ', ' بِأَسْيافِنَا وَالشَّاهِدُونَ بَنُو بَدْرِ )', 1),
('وقال خُفافُ بن نَدَبةَ ', 94, ' يَزيدُ نَبَالَةً عَنْ كلِّ شَيْءٍ ', ' وَنَافِلَةً وَبَعْضُ الْقوْمِ دُونُ )', 1),
('وقال معبد بن علقمة ', 95, ' فَقُلْ لِزُهَيْرٍ إنْ شَتَمْتَ سَرَاتَنا ', ' فَلَسْنَا بِشَتَّامِينَ لِلْمُتَشَتِّمِ )', 1),
('وقال حريث بن عناب بن مطر بن سلسلة بن كعب بن عوف تقدمت ترجمته', 96, ' لمَّا رَأيْتُ الْعَبْدَ نَبْهَانَ تَارِكي ', ' بِلَمَّاعَةٍ فِيهَا الْحَوادِثُ تَخْطُرُ )', 1),
('وقال أبان بن عبدة', 97, ' بِبِيضٍ خِفَافٍ مُرْهَفَاتٍ قَوَاطِعٍ ', ' لِدَاوُدَ فِيهَا أثْرُهُ وَخَوَاتِمُهْ )', 1),
('قال أبو هلال قراد بن عباد وقع هكذا في الأصل وهو خطأ وإنما هو قراد بن العيار بن محرز بن خالد أحد بني رزام وأبو العيار أحد شياطين العرب وقراد شاعر إسلامي مقل ', 98, ' وَلَمْ يَحْبُهُ بِالنصْرِ قَوْمٌ أعِزَّةٌ ', ' مَقَاحِيمُ فِي الأَمْرِ الَّذِي يُتَهَيَّبُ )', 1),
('وقال الفرزدق ', 99, ' لاَ قَوْمَ أكْرَمُ مِنْهُمْ يَوْمَ قالَ لَهُمْ ', ' مُحَرِّضُ الْمَوْتِ عَنْ أحْسَابِكُمْ ذُودُوا )', 1),
('وقال آخر', 100, ' قدْ عَلِمَ الْمُسْتِأْخِرُونَ في الْوَهلْ ', ' إذا السُّيُوفُ عُرِّيَتْ مِنَ الْخِللِ )', 1),
('وقال درَّاج وكان قد طعن', 101, ' هِيمٌ بِهيمٍ طُلِيَتْ تَمَرَّسْ ', ' )', 1),
('وقال سَوَّار', 102, ' سَعةَ الطّريقِ مَخافة أنْ يُؤْسَرُوا ', ' والخَيْلُ تَتْبَعُهُمْ وهُمْ فُرَّارُ )', 1),
('وقال أوس بن ثعلبة', 103, ' جَذّامُ حَبْلِ الْهَوَى ماضٍ إذَا جَعلَتْ ', ' هَواجِسُ الْهَمِّ بَعْدَ النَّوْمِ تَعْتَكِرُ )', 1),
('وقال الهُذْلول بن كعب العنبري ', 104, ' تَقولُ وصَكَّتْ نَحْرَها بِيَميِنَها ', ' أبَعْلِيَ هَذَا بِالرَّحَا الْمُتَقاعِسُ )', 1),
('وقالت كنزة أم شملة بن برد المِنقَري', 105, ' إنْ يَكُ ظَنِّي صَادِقاً وَهْوَ صادِقِي ', ' بِشَمْلَةَ يَحْبِسْهُمْ بِهَا مَحْبِساً أزْلاً )', 1),
('وقال قَبيصة بن جابر ', 106, ' أقِيمُوا صُدُورَ الْخَيْلِ إنَّ نُفُوسَكُمْ ', ' لِميقَاتِ يوْمٍ مَا لَهُنَّ خُلوفُ )', 1),
('وقال سالم بن وابصة ', 107, ' وَمَوْقِفٍ مِثْلَ حَدِّ السَّيْفِ قُمْتُ بِهِ ', ' أحْمي الذِّمارَ وَترْمِيني بهِ الْحَدَقُ )', 1),
('وقال الأخنس ', 108, ' تُمَشِّي بِهَا حُولُ النَّعَامِ كَأَنَّهَا ', ' إمَاءٌ تُزَجَّى بِالْعَشِيِّ حَوَاطِبُ )', 1),
('وقال العُدَيل بن الفُرْخ العجليُّ ', 109, ' بني قبة الإسلام حتى كأنما ', ' هدى الناس من بعد الضلال رسول )', 1),
('وقال عبد القيس بن خفاف البُرْجُميّ ', 110, ' صَحَوْتُ وَزَايَلَني باطِلِي ', ' لَعَمْرُ أبيكَ زِيَالاَ طَوِيلاَ )', 1),
('قال أبو رياش هي من بني قشير ', 111, ' فإِنْ يَكُ ظَنِّي صادِقاً وَهْوَ صادِقي ', ' بِكُمْ وَبِأَحْلاَمٍ لَكُمْ صَفِراتِ )', 1),
('وقال ابن السليماني ', 112, ' لَعَمْري لَقَدْ كَانَتْ فِجَاجٌ عَريضَةٌ ', ' ولَيْلٌ سخامِيُّ الْجَناحَينِ أدْهَمُ )', 1),
('وقال قتادة بن مَسلمة الحَنفي ', 113, ' يَمْلأُ عَيْنَيْكَ بِالْفِناءِ ويُرْضِيكَ ', ' عِقاباً إنْ شِئْتَ أوْ نزَقا )', 1),
('قال جُرَيْبَةُ بن الأشْيم الفَقْعَسيُّ ', 114, ' إذَا الْخَيْلُ صاحَتْ صيِاحَ النُّسُورِ ', ' حَزَزْنا شَراسِيفَها بِالْجِذَمْ )', 1);
INSERT INTO `poems` (`title`, `verse_number`, `misra1`, `misra2`, `book_id`) VALUES
('وقال أبو العطاء السندي ', 115, ' فإنْ تُمْسِ مَهْجُورَ الْفِناءِ وَرُبَّما ', ' أقامَ بهِ بَعْدَ الْوُفُودِ وُفُودُ )', 1),
('وقال رجل من خثعم ', 116, ' نَهِلَ الزَّمَانُ وَعَلَّ غَيْرَ مُصَرَّدِ ', ' مِنْ آلِ عِتَّابٍ وَآلِ الأسْوَدِ )', 1),
('وقال أيضًا', 117, ' أقُولُ وَما يَدْري أُناسٌ غَدْوَا بِهِ ', ' إلى اللّحدِ ماذَا أدْرَجُوا فِي السَّبائِبِ )', 1),
('وقال تأبط شرًّا ', 118, ' قَسَمْنا بِذَاكَ الدَّهرَ شطْرَيْنِ بَيْنَنا ', ' فَما يَنْقَضي إلاَّ وَنَحْنُ عَلى شَطْرِ )', 1),
('وقال سُيَودُ المرَاثِدِ الحارثي', 119, ' فَتًى قَبَلٌ لمْ تُعْنِسِ السِّنُّ وَجْهَهُ ', ' سِوَى خُلْسةٍ في الرّأْسِ كالبَرْقِ في الدُّجَى )', 1),
('وقال الحريث بن زيد الخيل ', 120, ' بأشَدِّهِمْ كَلَباً عَلى أعْدَائِهِمُ ', ' وَأعَزِّهِمْ فَقْداً عَلى الأصْحابِ )', 1),
('وقال أبو حِناك البراءُ بنِ ربعيّ الفقعسي', 121, ' ثَمانَيِةٌ كانُوا ذُؤَابَةَ قَوْمِهِمْ ', ' بِهِمْ كُنْتُ أُعْطِي ما أشَاءُ وأمْنَعُ )', 1),
('وقال بعض بني أسد', 122, ' بَكّى عَلى قَتْلَى العَدَانِ فإنّهُمْ ', ' طَالَتْ إقامَتُهُمْ بِبطْنِ بَرَامِ )', 1),
('وقال الشَّمَرْدَلُ بنُ شَرِيك ', 123, ' وَلَوْلاَ الأُسَى مَا عِشْتُ في النَّاسِ سَاعَةً ', ' ولَكنْ إذ مَا شِئتُ جاوَبَنيِ مِثْلي )', 1),
('قال الأسود بن عبد يغوث بن المطلب بن نوفل ', 124, ' ألاَ قَدْ سادَ بَعْدَهُمُ رِجالٌ ', ' وَلَوْلاَ يَوْمُ بَدْرٍ لَمْ يَسُودُوا )', 1),
('وقال عبد الملك بن عبد الرحيم الحارثي يكنى أبا الوليد ', 125, ' فكُنْتُ كَمَغْلُوبٍ عَلى نَصْلِ سَيْفِهِ ', ' وَقَدْ حَزَّ فِيهِ نَصْلُ حَرَّانَ ثَائرِ )', 1),
('وقال عُتيّ بنُ يزيد بن مالك العُقيليّ', 126, ' أعَدَّاءُ ما وَجْدِي عَليكَ بِهَيِّن ', ' وَلا الصَّبْرُ إنْ أُعْطِيتُهُ بجَميل )', 1),
('وقال خلف بن خليفة ', 127, ' أُعاتِبُ نَفْسِي أنْ تَبسَّمْتُ خالِياً ', ' وَقدْ يَضْحَكُ المَوْتُورُ وَهْوَ حزيِنُ )', 1),
('وقال أرْطأة بنُ سُهيّة المُرّيُّ ', 128, ' أخِلاَّيَ لَوْ غَيْرُ الحَمامِ أصابَكُمْ ', ' عَتَبْتُ وَلكِنْ ما عَلَى المَوْتِ مَعْتَبُ )', 1),
('وقال آخر في ابن له', 129, ' أُلاَمُ عَلى تَبَكِّيهِ ', ' وَألْسُهُ فلاَ أجِدُهْ )', 1),
('قال محمد ابن سلام الصحيح أن هذه الأبيات لعمرو بن شقيق أحد بني فهر بن مالك ومن الناس من يرويها لكرز بن حفص بن الأخيف العامري وعمرو بن شقيق أولى بها وهذا الشعر قيل في قتل ربيعة بن مكدم الكناني أحد فرسان مضر المعدودين وشجعانهم المشهورين قتله نبيشة بن حبيب السلمي في يوم الكديد ', 130, ' لوْلا السِّفارُ وبُعْدُ خَرْقٍ مَهْمَةٍ ', ' لتَرَكْتُها تَحْبُو بأرْبَعةٍ عَلى العُرْقُوبِ )', 1),
('وقالت أيضاً', 131, ' وَإذا دَعَتْ قُمْريَّةٌ شَجَناً لَهَا ', ' يوْماً عَلى فَنَنِ دَعَوْتُ صَباحِي )', 1),
('وقال الحجناء مولى بني أسد', 132, ' أعاذِلَ مَنْ يُرْزَأْ كَحَجْناءَ لاَ يَزَلْ ', ' كئيباً وَيزهَدْ بَعْدَهُ في العَوَاقِب )', 1),
('وقال أبو الشَّغْب العبسي في خالد بن عبد الله القسري ', 133, ' لعمْرُكَ ما وَارَى التُّرَابُ فَعالَهُ ', ' وَلكِنَّما وَرَاى ثِياباً وأعْظُما )', 1),
('وقال آخر', 134, ' تَظَلُّ بَنَاتُ الْعَمِّ وَالْخَالِ حَوْلَهُ ', ' صَوَادِيَ لاَ يَرْوَيْنَ بِالْبارِدِ الْعَذْبِ )', 1),
('وقال الحسين بن مطير بن الأشيم الأسدي ', 135, ' قلدته عرى الأمور نزار ', ' قبل أن يهلك السراة البحور )', 1),
('وقال أشجع بن عمرو السلمي في محمد بن منصور بن زياد ', 136, ' أنْعَى فَتَى الْجُودِ إلَى الْجُودِ ', ' مَا مِثْلُ مَنْ أنْعَى بمَوْجُودِ )', 1),
('وقال مسلم بن الوليد ', 137, ' فَلاَ وَجْدَ حَتَّى تَنْزفَ الْعَيْنُ مَاءَهاَ ', ' وَتَعْتَرِفَ الأَحْشَاءُ بِالْخَفَقانِ )', 1),
('وقالت صفية الباهلية ترثي أخاها', 138, ' لَوْ أنَّ خَيْرَكَ كانَ شَراًّ كُلُّهُ ', ' عِنْدَ الذِينَ عدَوْا عَلَيْكَ لَما عَدَا )', 1),
('وقال يزيد بن عمرو الطائي', 139, ' وقائِلَةٍ مَنْ أمَّها طَالَ لَيْلُهُ ', ' يَزِيدُ بْنُ عَمْرٍو أمّها فَاهْتَدَى لَها )', 1),
('قال سليمان بن قتَّة العَدوي ', 140, ' وَكانوا غِياثاً ثُمَّ أضْحَوْا رَزِيَّةً ', ' ألا عَظُمَتْ تلْكَ الرَّزَايَا وَجَلَّتِ )', 1),
('وقال النابغة الْجَعْدِيُّ ', 141, ' وَالنَّضْر أقْرَبُ مَنْ أصَبْتَ وَسيلةً ', ' وأحَقُّهُمْ إنْ كانَ عِتْقٌ يُعْتَقُ )', 1),
('وقال آخر', 142, ' لَعَمْرِي لَئِنْ سُرَّ الأعادِي فَأظْهَرُوا ', ' شَمَاتاً لَقدْ مَرُّوا بِرَبْعِكَ خَالِيَا )', 1),
('وقال كعب بن زهير ', 143, ' إذَا انْتَضلَ الْقَوْمُ الأحَادِيثَ لَمْ يَكُنْ ', ' عَيِيا وَلاَ رَبًّا عَلى مَنْ يُقَاعدُ )', 1),
('وقال آخر', 144, ' خَفيِفَ الْحَاذِ نَسَّالَ الْفَيافِي ', ' وَعَبْداً لِلصَّحابَةِ غَيْرَ عَبْدِ )', 1),
('وقال آخر ', 145, ' أضْحَى قِرًى لِلْمَنايا رَهْنَ بَلْقَعةٍ ', ' وقَدْ يَكُونُ غَداةَ الرَّوْعِ يَقْريها )', 1),
('وقال الربيع بن زياد في مالك بن زهير العبسي ', 146, ' أولاَكَ بَنو خَيرٍ وَشّرٍ كِلَيْهِما ', ' جَميعاً ومَعْروفٍ ألَمَّ ومُنْكَرِ )', 1),
('وقال غُويَّةُ بنُ سُلْميِّ بن ربيعةَ', 147, ' وبعْد أبي ربيعة عبدِ عمروٍ ', ' ومسْعُودٍ وبعد أبي هلال )', 1),
('وقال المِسْجاحُ بنُ سِباع الضَّبيُّ ', 148, ' وَشَهْرٌ مُسْتَهَلٌّ بَعْدَ شَهْرٍ ', ' وحَوْلٌ بَعْدَهُ حَوْلٌ جَدِيدُ )', 1),
('وقال زُوَيْهِرُ بنُ الحرث بن ضِرار ', 149, ' وَكَانَ عَمِيدَنا وَبَيْضَةَ بَيْتِنا ', ' فَكُلُّ الَّذِي لاَقَيْتُ مِنْ بَعْدِهِ جَلَلْ )', 1),
('وقال إياس بن الأرتِّ ', 150, ' وَحانَ فِراقٌ مِنْ أخٍ لكَ ناصِحٍ ', ' وَكانَ كَثيرَ الشَّرِّ لِلْخَيْرِ تَوْأمَا )', 1),
('وقال الغَطَمَّشُ من بني شقرة بن كعب بن ثعلبة بن سعد بن ضبة', 151, ' عَلى رِشْدَةٍ مِنْ أُمِّهِ أوْ لِغَيَّةِ ', ' فَيَغْلِبَها فَحْلٌ عَلى النَّسْلِ مُنْجِبُ )', 1),
('قال أبو هلال في الشعراء ثلاثة يقال لهم القلاخ أحدهم القلاخ الراجز بن حزن بن جناب بن منقر والآخر القلاخ بن زيد أحد بني عمرو ابن مالك والثالث القلاخ العنبري أحد بني العنبر والمراد هنا القلاخ بن حزن المنقري وهو شاعر إسلامي مجيد مقل ', 152, ' وكنت إذا ما شئت سنيت والدا ', ' يزين كما زان اليدين الأساور )', 1),
('وقال الضَّبيّ', 153, ' وَلَرُبَّ عَانٍ قَدْ فَكَكْتَ وَسائِلٍ ', ' أعْطَيْتَهُ فَغَدَا وَأنْتَ حَمِيدُ )', 1),
('وقال لبيد ', 154, ' أخاً لَي أمَّا كُلُّ شَيءٍ سَألْتُهُ ', ' فَيُعْطِي وَأمَّا كُلَّ ذَنْبٍ فيغْفِرُ )', 1),
('وقال مُنْقِذٌ الهِلاَلِيُّ ', 155, ' وَلخَيْرُ حَظِّكَ فِي المُصِيبَةِ أن ', ' يَلْقاكَ عِنْدَ نُزُولِها الصَّبْرُ )', 1),
('وقال رجل من بني أسد', 156, ' أبْعَدْتَ مِنْ يَوْمِكَ الْفِرَارَ فَما ', ' جاوزْتَ حَيْثُ انْتَهَى بِكَ الْقدَرُ )', 1),
('وقال النابغة الجعدي تقدمت ترجمته', 157, ' فتًى تَمَّ فِيهِ مَا يَسُرُّ صَدِيقَهُ ', ' عَلى أنّ فِيه مَا يَسُوءُ الأعَادِيَا )', 1),
('وقال ابن أُهْبانَ الفقعسيُّ يرثي أخاه', 158, ' إذَا نازعَ الْقَوْمَ الأحادِيثَ لَمْ يَكُنْ ', ' عَيِيًّا ولاَ رَبًا عَلى مَنْ يُقاعِدُ )', 1),
('وقال رجل من كلب', 159, ' لَحَا اللهُ دَهْرًا شَرُّهُ قَبْلَ خَيرِهِ ', ' وَوجدًا بَصَيْفِيٍّ أتَى بَعْدَ مَعْبَدِ )', 1),
('قال الأبَيرِدُ اليَرْبوعي ', 160, ' وَسامَى جَسيمَاتِ الأُمُورِ فَنالَها ', ' عَلى الْعُسْرِ حتَّى أدْرَكَ الْعُسُرَ الْيُسْرُ )', 1),
('وقالت عمْرَةُ الْخَثْعَمِيَّةُ ترثي ابنيها', 161, ' هُمَا أخْوَافي الْحَرْبِ مَنْ لاَ أخَالَهُ ', ' إذَا خافَ يَوْماً نَبْوَةً فَدَعاهُمَا )', 1),
('وقال آخر', 162, ' نِعْمَ الْفَتى زَعَمَ الرَّفِيقُ وَجارُهُ ', ' وَإذَا تَصَبْصَبَ آخِرُ الأَزْوَادِ )', 1),
('وقالت ريطةُ بِنتُ عاصم', 163, ' وَلوْ أنَّ سَلْمى نَالَها مِثْلُ رُزْئِنا ', ' لَهُدَّتْ ولَكنْ تَحْمِلُ الرُّزْءَ عَامِرُ )', 1),
('وقال امرأة من طيىء', 164, ' هوَ الأبْيَضُ الوضَّاحُ لوْ رُمِيَتْ بِهِ ', ' ضَوَاحِ مِنَ الرِّيَّانِ زَالتْ هِضابُها )', 1),
('وقال جَريرُ يرثي قَيس بن ضِرَار بن الْقَعقَاع بن مَعبَدٍ بن زُرَارةَ ', 165, ' غَيرَ أنَّ الْبأْسَ مِنهُ شِيمةٌ ', ' وصُرُوفُ الدَّهْر تَجْري بِالأجَلْ )', 1),
('وقال آخر يرثي ابنه', 166, ' فإنْ أبْكِ أبْكِ عَلى فَاجِعٍ ', ' وَإنْ يَكُ صَبرٌ فَمثْلِي صَبَرْ )', 1),
('وقال يحيى بن زياد تقدمت ترجمته', 167, ' وَلكِنْ إذَا مَا حَلَّ كُرْهٌ فَسامَحَتْ ', ' بِهِ النَّفْسُ يَوْمًا كانَ لِلْكُرْهِ أذْهَبا )', 1),
('وقال شبيب بن البرصاء المري ', 168, ' فَقَدْ جَعَلْتُ إذَا مَا حَاجَتِي نَزَلَتْ ', ' بِبابِ دَارِكَ أدْلُوهَا بأقْوَامِ )', 1),
('وقال مَعْنْ بن أوس', 169, ' تأوّبه طيف بذات الجرائم ', ' فنام رفيقاه وليس بنائم )', 1),
('وقال ربيعة بن مَقرُوم الضبي ', 170, ' إذَا زُرْتُ أرْضاً بَعْدَ طُولِ اجْتِنابهَا ', ' فَقَدْتُ صَدِيقِي والْبِلاَدُ كمَا هِيَا )', 1),
('وقال شبيب بن البرصاء المري تقدمت ترجمته', 171, ' إِذا المَرْءُ أعرَاهُ الصَّدِيقُ بَدَا لهُ ', ' بأرْض الأعادِي بَعْضُ ألْوَانِها الرُّبْدِ )', 1),
('وقال بعض الفزاريين', 172, ' وكُنْ أكْيَسَ اْلكَيْسَى إذَا كُنْتَ فِيهِمِ ', ' وإنْ كُنْتَ فِي الْحَمْقَى فَكُنْ أنْتَ أحْمَقَا )', 1),
('وقال آخر ', 173, ' عَسَى سَائلٌ ذُو حَاجَةٍ إنْ مَنَعْتَهُ ', ' مِنَ الْيَوْمِ سُؤْلاً أنْ يَكُونَ لهُ غَدُ )', 1),
('وقال مَنْظُورُ بنُ سُحَيم ', 174, ' فأنقذني منها حماري وجبتي ', ' جزى الله خيرا جبتي وحماريا )', 1),
('وقال آخر', 175, ' وَأُعرِضُ عَنْ مَطَاعِمَ قَدْ أرَاها ', ' فأترُكُها وَفي بَطْني انْطِواءُ )', 1),
('وقال آخر', 176, ' وإنّي لأنْسَى عِنْدَ كُلّ حَفِيظَةٍ ', ' إذا قِيلَ مَوْلاكَ احْتِمال الضَّغائنِ )', 1),
('وقال مالك بن حريم الهمدانيُّ ', 177, ' أُنْبئتُ وَالأَيَّامُ ذاتُ تجَاربٍ ', ' وتُبْدي لكَ الأَيَّامُ مَا لسْتَ تعْلمُ )', 1),
('وقال أيضاً والوزن كالأول', 178, ' لا تَيْأسَنَّ وَإنْ طَالَتْ مُطالَبَةٌ ', ' إذَا اسْتَعنْتَ بِصَبرٍ أنْ تَرَى فَرَجَا )', 1),
('قال المقَنَّع الكِنْدِيُّ ', 179, ' كأنها من جلود الباخلين بها ', ' عند النوائب تحذى بالمقاريض )', 1),
('وقال رجل من الفزاريين', 180, ' إذا كُنْتُ في الْقوْمِ الطِّوَالِ عَلوْتُهُمْ ', ' بِعارِفَةٍ حَتَّى يُقالَ طَوِيلُ )', 1),
('وقال مُضَرّسُ بنُ رِبْعيِّ ', 181, ' فإنك لا تعطي امرأ حق غيره ', ' ولا تعرف الشق الذي الغيث ماطره )', 1),
('قال أبو رياش هذه الأبيات للربيع بن أبي الحقيق اليهودي أما قيس بن الخطيم فقد تقدمت ترجمته وأما الربيع بن أبي الحقيق فإنه كان شاعرا من شعراء ', 182, ' وَلكنَّني لَمْ أنْسَ مَا قالَ صاحِبي ', ' نَصيبَكَ منْ ذُلٍّ إذَا كُنْتَ خالِيا )', 1),
('وقال يزيد بن الحكم الثقفي يعظ ابنه بدرا ', 183, ' ورثت جدي مجده وفعاله ', ' وورثت جدك أعنزا بالطائف )', 1),
('وقال مُنْقِدٌ الهلالي', 184, ' وَالْخَيْلُ أجْوَدُها الْمُناهِبُ ', ' عِنْدَ كَبَّتِها الأَزُومُ )', 1),
('وقال آخر', 185, ' تَجَلَّلْتَ عَاراً لاَ يَزَالُ يَشُبُّهُ ', ' سِبَابُ الرِّجالِ نَثرُهُمْ وَالْقصَائِدُ )', 1),
('وقال الْحَكَمْ بن عبدلٍ', 186, ' فَأفٍّ لدُنْيا لاَ يَدُومُ نَعِيمُها ', ' تَقَلَّبُ تَارَاتٍ بِنا وَتصَرَّفُ )', 1),
('وقال الفرزدق تقدمت ترجمته', 187, ' فقُلْ لِلشَّامِتينَ بِنا أفيقُوا ', ' سَيَلْقَى الشَّامِتونَ كمَا لَقِينَا )', 1),
('وقال حسان بن ثابت الأنصاري ', 188, ' كمَا الصَّمْتُ أدْنَى لِبَعْض الرَّشادِ ', ' فَبعْضُ التَّكلمَ أدْنَى لَغِي )', 1),
('قال الصّمة بن عبد الله بن طفيل بن الحرث بن قرَّةَ بن هبيرة ابن عامر بن سلمة الخير بن قشير بن كعب ', 189, ' أحْتَالُ لِلْمالِ أنْ أُودِي فأكْسِبُهُ ', ' ولَسْتُ لِلْعرْضِ أنْ أُودِي بِمُحتال ', 1),
('وقال آخر ', 190, ' وَأذْكُرُ أيَّامَ الْحِمَى ثُمَّ أنْثَنِي ', ' عَلى كَبِدِي مِنْ خَشْيَةٍ أنْ تَصَدَّعَا )', 1),
('وقال آخر ', 191, ' يوْمَ ارْتَحَلْتُ بِرَحْلي قبْلَ برْذَعَتي ', ' والْعَقْلُ مُتَّلةٌ والْقَلْبُ مَشْغُولُ )', 1),
('وقال الحسين بن مَطير الأسدي تقدمت ترجمته', 192, ' مُخَصَّرَةُ الأوْسَاطِ زَانَتْ عُقُودَها ', ' بأحْسَنَ ممَّا زَيَّنَتْها عُقُودُها )', 1),
('وقال أيضاً', 193, ' إنِّي أرَى وأظَنُّ أنْ سَتَرَى ', ' وَضَحَ النَّهارِ وَعالِيَ النَّجْمِ )', 1),
('وقال آخر', 194, ' أمَا وَالَّذِي حَجَّتْ لَهُ الْعِيسُ تُرْتَمى ', ' لِمَرْضاتِهِ شُعْثٌ طَويلٌ ذَميلُها )', 1),
('وقال آخر ', 195, ' وَمِمَّا شَجانِي أنَّها يوْمَ أعْرَضتَ ', ' تَوَلَّتْ وَماءُ الْعينِ في الْجَفْنِ حائِرُ )', 1),
('وقال ابن هَرْمةَ ', 196, ' قُلْتُ لَبَّيْكِ إذْ دَعانِي لَكِ الشَّوْقُ ', ' وَلِلْحَادِيَيْن حُثَّا الْمَطِيَّا )', 1),
('وقال آخر ', 197, ' ألاَ أيُّهَا الْقَلْبُ الَّذِي قادَهُ الْهَوَى ', ' أفِقْ لاَ أقَرَّ اللهُ عَيْنَكَ منْ قَلبِ )', 1),
('وقال عبد الله بن عجلان النهدي', 198, ' مراجِعُ نَجْدٍ بَعْدَ فِرْكٍ وَبِغْضَةٍ ', ' مُطَلِّقُ بُصْرَى أصْمَعُ القَلبِ جافِلُهْ )', 1),
('وقال عبد الله بن الدمينة الخثعمي تقدمت ترجمته ) ', 199, ' وَلمَّا لَحِقْنا بِالْحَمُولِ وَدُونَهَا ', ' خَمِيصُ الْحَشَا تُوهِي الْقَمِيص عَوَاتِقُهْ )', 1),
('قال أبو الطَّمَحان القيني', 200, ' وَقبْلَ غَدٍ يا لَهْفَ نَفْسي عَلى غَدٍ ', ' إذَا رَاحَ أصْحابِي ولَسْتُ بِرائِحِ )', 1),
('وقال شبْرُمَةُ بن الطفيل', 201, ' كَأَنَّ أبارِيقَ الشَّمُولِ عَشِيَّةً ', ' إوَزَّ بِأَعلَى الطَّفِّ عُوجُ الْحناجِرِ )', 1),
('وقال إياسُ بنُ الأرَت الطَّائيّ', 202, ' نُسَلِّ مَلاَماتِ الرِّجَال بِرِيَّةٍ ', ' ونَفْرِ شُرَورَ الْيَوْمِ بِاللَّهْوِ وَاللِّعْبِ )', 1),
('وقال الحرث بن خالد المخزومي ', 203, ' تبعتك إذ عيني عليها غشاوة ', ' فلما انجلت قطعت نفسي ألومها )', 1),
('وقال بَكرُ بنُ النَّطَّاح', 204, ' وَإذا الرِّياحُ مَعَ الْعَشِيِّ تَناوَحَتْ ', ' نَبَّهْنَ حَاسِدَةً وَهِجْنَ غَيُورَا )', 1),
('وقال كُثَيّرُ بنُ عبد الرحمن ', 205, ' وَما ذَكَرَتكِ النَّفْسُ إلاَّ تَفَرَّقَتْ ', ' فَرِيقَيْنِ منْهَا عَاذِرٌ لِي وَلاَئمُ )', 1),
('وقال آخر', 206, ' أرَارَ اللهُ نِقْيَكِ فِي السَّلاَمَى ', ' عَلى مَنْ بِالْحَنِينِ تُعَوِّلينَا )', 1),
('وقال عروة بن أُذينة تقدمت ترجمته', 207, ' لاَ يُعْجَبَانِ بِقَوْلِ النَّاسِ عَنْ عُرُضٍ ', ' وَيُعْجَبَانِ بِما قالاَ وَما صَنَعا )', 1),
('وقال عبد الله بن الدُّمينة الخثعمي', 208, ' بَكَيتَ كمَا يَبْكي الْوَليدُ ولَمْ تكنْ ', ' جَليدًا وأبْدَيتَ الذِي لَمْ تكُنْ تُبْدِي )', 1),
('وقال آخر ', 209, ' تَناهَيْتِ عَنِّي حينَ لاَ لِيَ حِيلْةٌ ', ' وغادَرْتِ مَا غَادَرْتِ بَينِ الْجَوَانح )', 1),
('وقال آخر ', 210, ' سَلى الْبانَة الْغَيناءَ بالأَجْرَعِ الَّذي ', ' بِهِ الْبانُ هَلْ حَيَيَّتُ أطْلالَ دَاركِ )', 1),
('وقال آخر وقيل هو عتيبة بن مرداس', 211, ' أرَادَتْ لِتَنْتاشَ الرِّوَاقَ فَلَمْ تَقُمْ ', ' إليْهِ وَلكِنْ طَأْطَأتْهُ الْوَلائِدُ )', 1),
('وقال نُصَيْب تقدمت ترجمته', 212, ' إذا سَمِعا هُبوبَ الرِّيحِ نَصَّا ', ' وقَدْ أودَى بِهِ القَدَرُ المُتاحُ )', 1),
('وقال الحكم الخُضريُّ ', 213, ' يُذَكِّرُنيكِ الْخَيرُ وَالشَّرُّ وَالذِي ', ' أخافُ وأرْجُو وَالذي أتَوَقَّعُ )', 1),
('وقال آخر في هذا الوزن', 214, ' عَفا اللهُ عَنْ ليْلَى الْغَدَاةَ فإِنَّها ', ' إذا وَليَتْ حُكْماً عَليَّ تَجورُ )', 1),
('وقال سَعْدَانُ بن المُضرَّب الكِنْدِيّ', 215, ' أجدَّ لنَا طِيبُ المَكانِ وحُسْنُهُ ', ' مُنىً فتَمَنَّيْنا فكُنْتِ الأمانِيا )', 1),
('وقال آخر ', 216, ' فلاَ تَحْسبِي أنَّ الْغرِيبَ الَّذي نأَى ', ' وَلكِنَّ مَنْ تَنْأَيْنَ عنْهُ غرِيبُ )', 1),
('وقال آخر ', 217, ' هَلِ الحُبُّ إلاَّ زَفرَةٌ بعْدَ زَفْرَةٍ ', ' وَحرّ عَلى الأحْشاءِ ليْسَ لهُ بَرْدُ )', 1),
('وقال آخر ', 218, ' يقُولُ العِدَا لاَ بارَكَ اللهُ فِي الْعِدَا ', ' قدْ اقْصَرَ عنْ ليلَى ورَثَّتْ وَسائِلُهْ )', 1),
('وقال أبو الأسود الدؤلي ', 219, ' فقُلْتُ لَها ما كُنْتُ أوَّلَ ذِي هَوًى ', ' تَحَمَّلَ حِمْلاً فادِحاً فَتُوَجَّعا )', 1),
('وقال جميل ', 220, ' كأنْ لمْ يَكُنْ بَينٌ إذا كانَ بَعْدَهُ ', ' تَلاَقٍ وَلكِنْ لاَ إخَالُ التَّلاَقِيا )', 1),
('وقال أبو دَهْبَلٍ الجُمَحِيُّ تقدمت ترجمته', 221, ' إنْ كانَ ذَا قَدَراً يُعْطيكِ نَافِلةً ', ' مِنَّا ويَحرمُنا مَا أنصَفَ الْقَدَرُ )', 1),
('وقال عُبيد الله بن عبد الله بن عتبة بن مسعود ', 222, ' شَقَقْت القَلْبَ ثُمَّ ذَرَرْتِ فِيهِ ', ' هَوَاكِ فَليمَ فالْتأمَ الفُطُورُ )', 1),
('وقال آخر ', 223, ' مِنْ مُحْذياتِ أخى الهَوَى جُرَعَ الأسَى ', ' بِدَلالِ غانِيِةٍ ومُقْلةِ رِيمِ )', 1),
('وقال سوَّار بنُ المضرَّب', 224, ' وَحاجَةٍ دُونَ أُخرَى قَدْ سَنَحْتُ بهَا ', ' جَعَلْتُها لِلَّتي أخْفَيْتُ عُنْوَانا )', 1),
('قال أبو الشّيص الْخُزاعي ', 225, ' أشْبَهْتِ أعْدَائي فَصِرْتُ أُحِبُّهُمْ ', ' إذْ كانَ حَظِي مِنْكَ حَظّي مِنْهُمُ )', 1),
('وقال أبو القَمْقام الأسدي', 226, ' اقْرَأْ عَلى الوَشَل السَّلاَمَ وقُلْ لهُ ', ' كُلُّ الْمَشارِبِ مُذْ هُجِرْتَ ذَميمُ )', 1),
('وقال المعْلوطُ بنْ بدل السَّعْدِيّ', 227, ' غَيَّضْنَ مِنْ عَبَراتِهِنَّ وقُلْنَ لِي ', ' ماذَا لَقِيتَ مِنَ الْهَوَى ولَقينَا )', 1),
('قال أبو رياش هي لعمرو بن الأيهم ', 228, ' ومَا بَرِحَ الْواشُونَ حتَّى ارْتَمَوا بِنا ', ' وحتَّى قُلوبٌ عَنْ قُلوبٍ صَوادِفُ )', 1),
('وقال زياد بن حمل بن سعد بن عميرة بن حريث ', 229, ' وَلنْ أُحِبَّ بِلاداً قدْ رأيْتُ بِها ', ' عَنْساً ولاَ بَلداً حَلَّتْ بهِ قُدُمُ )', 1),
('وقال عمرو بن ضبيعة الرَّقاشِيّ ', 230, ' وغُصّةِ صَدْرٍ أظَهرتَهَا فَرَفَّهتْ ', ' خَزَارَةَ حرٍّ في الْجوَانِحِ والصَّدْرِ )', 1),
('وقال مرداس بن همام الطائي', 231, ' ألاَ حبَّذَا لَوْمَا الْحَيَاءُ وَرُبّما ', ' مَنَحْتُ الهَوَى مَنْ لَيْسَ بالمُتَقارِب )', 1),
('وقال آخر ', 232, ' كأن فؤادي يوم جاء نعيها ', ' ملاءة قز بين أيد تطيرها )', 1),
('وقال ابن هرم الكلابي', 233, ' إنِّي عَلى طُولِ التَّجَنُّبِ والْهَوى ', ' وَوَاشٍ أتَاهَا بي وَواشٍ لَها عِندِي )', 1),
('وقال جميل تقدمت ترجمته', 234, ' بُثيْنةُ مَا فِيها إذا مَا تُبُصّرَت ', ' مَعابٌ ولاَ فِيهَا إذا نُسبَتْ أشْبُ )', 1),
('وقال موسى بن جابر الحنفي تقدمت ترجمته', 235, ' كانَتْ حَنيفَةُ لاَ أبَالَكَ مَرَّةً ', ' عِنْدَ اللّقَاءِ أسِنةً لاَ تَنْكُلُ )', 1),
('وقال عَملَّسُ بن عَقيل بن عُلَّفة ', 236, ' مَنْ مُبْلغٍ عَنِّي عَقِيلاً رِسَالةً ', ' فَإِنَّكَ مِنْ حَرْبٍ عَليَّ كرِيمُ )', 1),
('وقال زُميلُ بنُ أُبيرٍ ', 237, ' يقولون أبناء البعير وماله ', ' سنام ولا في ذروة المجد غارب )', 1),
('وقال عمارة بن عقيل ', 238, ' فإِنَّكَ واسْتِبْضاعَكَ الشِّعر نحْوَنا ', ' كَمُسْتَبْضعٍ تمْراً إلى أرْضِ خّيبْرَا )', 1),
('وقال بشير بن أبي بن جذيمة بن الحكم بن مروان بن زنباع بن جذيمة', 239, ' وإنَّ لِسانَ المَرْءِ مَا لَمْ تَكُنْ لهُ ', ' حَصَاةٌ عَلى عَوْرَاتِهِ لَدَلِيلُ )', 1),
('قال أبو رياش ليس هذا الشعر لعارق إنما هو لثرملة بن شعاث الأجئي على لسان عارق وسبب هذه الأبيات أن عمرو بن المنذر بن ماء السماء كان قد عاهد طيئا أن لا يغزوهم فاتفق أن عمرا غزا اليمامة فرجع مخفقا ومر بطيىء فقال له زرارة بن عدس أبيت اللعن أصب من هذا الحي فقال ويلك إن لهم عقدا فقال وإن كان فإنك لم تكتب العقد لهم كلهم فلم يزل به حتى أصاب نسوة وأزوادا فقال في ذلك قيس بن جروة ', 240, ' من مبلغ عمرو بن هند رسالة ', ' إذا استحقبتها العيس تنضى من البعد )', 1),
('وقال قَعْنَبُ بنُ أمِّ صاحِب ', 241, ' صُمّ إذَا سَمِعُوا خَيراً ذُكِرْتُ بِه ', ' وإنْ ذُكِرْتُ بِشَرٍّ عِنْدَهُمْ أذِنُوا )', 1),
('وقال مُحرِزُ بنُ الكَعبِر الضَّبيّ لبني عديّ بن جندب بن العَنبر ', 242, ' أبلغْ عَدِيًّا حَيْثُ صَارَتْ بِها النَّوَى ', ' وَليْسَ لِدَهْرِ الطّالِبينَ فَنَاءُ )', 1),
('وقال شَمْعلة بن الأخضر ', 243, ' وَلَوْ مَلأَتْ أعْفاجَها مِنْ رَثِيئَةٍ ', ' بَنُو هَاجِرٍ مالَتْ بِهَضْبِ الأكادِر )', 1),
('وقال معدان بن عبيد بن عدي بن عبد الله بن خَيبريّ بن أفْلَت الطّائي ثم المعنيُّ ', 244, ' نَهَيْتُكِ عنْهُ في الزَّمانِ الَّذي مَضَى ', ' وَلاَ يَنْتَهي الغَاويِ لأوَّلِ قيل )', 1),
('وقال آخر', 245, ' فهذَا أوَانُ الشِّعْرِ سُلَّتْ سِهامُهُ ', ' مَعَابِلُها وَالمُرْهَفاتُ السَّلاَجِمُ )', 1),
('قال أبو رياش تزوج عبد الله بن مدلج الطائي هنيدة بنت عبد الرحمن ابن حدير فأبت أن تنزله عندها فقال في ذلك أدهم بن أبي الزعراء هذه الأبيات ', 246, ' وإنّا لَمَحْقُوقُونَ حِينَ غضِبْتُمُ ', ' بأيْمةِ عبْدِ اللهِ أنْ سَنُهِينُها )', 1),
('وقال شُعَيْثُ بن عبدِ الله ', 247, ' فما كنانة في خير بخائرة ', ' ولا كنانة في شر بأشرار )', 1),
('وقال آخر', 248, ' بَنى أسَدٍ إلاَّ تَنَحَّوْا تَطأْكُمُ ', ' مَناسِمُ حَتّى تُحْطَمُوا وَحَوَافِرُ )', 1),
('وقال الطرماح بن جهم السّنبِسي لنافذ بن سعد المعنى', 249, ' إنَّ بِمَعْنٍ إنْ فَخَرْتَ لَمَفْخَراً ', ' وفِي غَيرِهَا تُبْنى بُيُوتُ المكارِمِ )', 1),
('وقال وضاح بن إسماعيل بن عبد كلاَلٍ بن داود بن أبي أحمد تقدمت ترجمته', 250, ' وإنْ قُلْتَ لاَ إلاَّ التَّفَرُّقَ والنَّوَى ', ' فَبُعْدًا أدَامَ اللهُ تَفْرِقَةَ النَّوَى )', 1),
('وقال أيضاً', 251, ' فلَوْ طَاوَعُوني يَوْمَ بُطْنان أُسْلِمتْ ', ' لِقَيْسٍ فَرُوجٌ مِنكُمُ ومقَاتِلُ )', 1),
('قال عبد الرحمن بن الحكم ', 252, ' بأبيض من أمية مضرحي ', ' كأن جبينه سيف صنيع )', 1),
('وقال في ذلك خَنزَرُ بن أرقم ', 253, ' بني قَطَنٍ مَا بَالُ نَاقَةِ ضَيْفِكُمْ ', ' تَعَشَّوْنَ مِنهَا وهْيَ مُلْقًى قُتُودُهَا )', 1),
('وقال آخر', 254, ' لاَ تَحْسَبِ المَجْدَ تَمْياً أنْتَ آكِلُهُ ', ' لَنْ تَبْلُغَ المْجْدَ حتَّى تَلْعَقَ الصَّبِرَا )', 1),
('وقال عبد الله بن أوفى الخزاعي في امرأته', 255, ' نَكَحْتُ ابنةَ المُنتَصَى نَكْحةً ', ' عَلى الكُرْهِ ضَرَّتْ ولم تَنفَعِ )', 1),
('وقال بعضُ آل المهلَّبِ قال دِعبِلٌ هوَ عبد الله بن عبد الرحمن ولقبه أبو الأنواء ', 256, ' لاَ يَقْبِسُ الْجَارُ مِنهُمْ فضْلَ نارِهِمِ ', ' وَلا تُكَفُّ يَدٌ عَنْ حُرْمةِ الجَارِ )', 1),
('قال مالك بن أسماء ', 257, ' منطق صائب وتلحن أحيانا ', ' وأحلى الحديث ما كان لحنا )', 1),
('وقال مُدْرِكُ أو مُغلسُ بنُ حِصنٍ الفقْعسِيّ', 258, ' تُشبَّهُ عبْسٌ هاشِماً أنْ تَسَرْبلَتْ ', ' سَرابيلَ خَزٍّ أنْكَرَتْها جُلودُها )', 1),
('وقال آخر ', 259, ' أناخَ اللَّؤْمُ وسْطَ بَني رِياحٍ ', ' مَطِيَّتَهُ فأقْسم لاَ يَريمُ )', 1),
('وقال آخر يهجو الحّضَريَّ ويمدح البدويَّ', 260, ' قوْمٌ إذا خرَجُوا منْ سَوْأةٍ ولَجُوا ', ' في سوْاةٍ لمْ يُجنُّوها بِأسْتارِ )', 1),
('وقال حجر بن حية العبسي', 261, ' وَلا أُدَوِّمُ قِدْرِي بَعْدَ مَا نَضِجَتْ ', ' بُخْلاً لِتَمْنَعَ مَا فِيهَا أثَا فِيهَا )', 1),
('وقال آخر', 262, ' إذَا قُلْتُ عُودُوا عَاد كلُّ شَمَرْدَلِ ', ' أشَمَّ مِنَ الْفِتْيَانِ جَزْلٍ مَوَاهِبُهْ )', 1),
('وقال حزاز بن عمرو من بني عبد مناف', 263, ' وَلكِنْ فَتَى الْفِتْيانِ مَنْ رَاحَ أوْ غَدَا ', ' لِضَرِّ عَدُوٍّ أوْ لِنَفْعِ صَدِيقِ )', 1),
('وقال عامر بن حوط من بني عامر بن عبد مناة بن بكر بن سعد بن ضبة', 264, ' وَأزُورُ بَيْتَ الْحَقِّ زَوْرَة مَاكِثٍ ', ' فَعَلاَم أحْفِلُ مَا تَقَوَّضَ وَانْهَدَمْ )', 1),
('وقال الهذيل بن مشجعة البولاني', 265, ' وَإذا تَتَبَّعَتِ الْجَلاَئِفُ مَا لَنا ', ' خُلِطَتْ صَحِيحَتُنا إلى جَرْبَائِهِ )', 1),
('وقال إياس بن الأرت', 266, ' وإنّي لَمِمَّنْ يَبْسُطُ الكَفَّ بِالنَّدَى ', ' إذَا شَنِجَتْ كَفُّ الْبَخِيلِ وسَاعِدُهْ )', 1),
('قُالَ عبد العزيز بن زرارة الكلابي', 267, ' الفَقْرُ يُزْرِي بِأقْوَامٍ ذَوِي حَسَبٍ ', ' ويَقْتَدِي بِلِئامِ الأصْلِ أنْذَالِ )', 1),
('وقال مضرس بن ربعي', 268, ' ومَنْ يًقْتَرِقْ خُلْقًا سِوَى خُلْقِ نَفْسه ', ' يَدَعْهُ وَترْجِعْهُ إليهِ الرَّوَاجِعُ )', 1),
('وقال آخر ', 269, ' وَما أنَا بِالسَّاعِي إلَى أُمِّ عَاصِمٍ ', ' لأَضْرَبهَا إنِّي إذاً لَجهُولُ )', 1),
('وقال آخر', 270, ' فَذُوا الْحِلْم مِنَّا جَاهِلٌ دُونَ ضَيْفِهِ ', ' وذُو الْجَهْلِ مِنَّا عَنْ أذَاهُ حَلِيمُ )', 1),
('وقال سالِم بن قُحْفان العنبَرِيّ ', 271, ' لقد بكرت أم الوليد تلومني ', ' ولم أجترم جرما فقلت لها مهلا )', 1),
('وقال قيس بن عاصِم المنقري ', 272, ' إنّي امرُوءٌ لاَ يَعْترِي خُلُقي ', ' دَنَسٌ يُفَنِّدُهُ وَلاَ أفْنُ )', 1),
('وقال طُريْحُ بن إسماعيلَ الثَقفيّ ', 273, ' فأرْجِعُ مَغبُوطًا وَترْجِعُ بالّتي ', ' لَها أوَّلٌ في المَكرُمَاتِ وآخِرُ )', 1),
('قال رجل من بَهراءَ واسمه فدَكيٌّ ', 274, ' لأحبَّني حُبَّ الصَّبيِّ ورَمني ', ' رَمَّ الْهدِيّ إلى الْغِنيّ الوَاجِد )', 1),
('وقال العَرندس ', 275, ' وَإنْ تَودَّدْتَهُمْ لانُوا وَإنْ شُهِمُوا ', ' كشَّفْتَ أذْمارَ شَرّ غيرَ أشْرارِ )', 1),
('وقال أبو الطمحان القيي واسمه حنظلة بن الشرقي تقدمت ترجمته', 276, ' إذَا قيلَ أيُّ النَّاسِ خَيْرٌ قبِيلَةً ', ' وأصبَرُ يوْماً لا تَوارَى كواكِبُهْ )', 1),
('وقال آخر ', 277, ' أجَلَّ جَلاَلةً وَأعَزَّ فَقْدًا ', ' وَأقضَى لِلْحُقُوقِ وَهُمْ قُعُودُ )', 1),
('وقال أبو دَهْبَلِ الجمحي يمدح النبي', 278, ' مُتَهَللٌ بِنَعَمْ بِلاَ مُتَبَاعِدٌ ', ' سِيّانِ مِنهُ الْوَفْرُ وَالعُدْمُ )', 1),
('وقالت أيضا ويقال بل قالها أبوها', 279, ' تبكي السُّيُوفُ إذا فَقَدْنَ أكُفَّنَا ', ' جَزَعًا وتَعْلَمُنا الزِّفَاقُ بُحُورَا )', 1),
('وقال آخر', 280, ' مثقفة صدورهما وشيفت ', ' صدور أسنة لهما حداد )', 1),
('وقال أيضًا', 281, ' بَعيدٌ منَ الشَّيءِ القَليلِ احْتِفاظُهُ ', ' عَلَيْكَ وَمنْزُورُ الرَّضِّا حِينَ يَغضَبُ )', 1),
('وقال أيضاً فيه', 282, ' حتَّى تَمَنىَّ البُرَاةُ أنَهُمُ ', ' عِنْدَك أمْسَوْا في القِدّ وَالْحَلَقِ )', 1),
('وقال آخر', 283, ' إذا انْتَدَى وَاحتَبَى بِالسَّيْفِ دَانَ لهُ ', ' شُوسُ الرِّجالِ خُضُوعَ الْجُرْبِ لِلطالِي )', 1),
('وقال عمرو بن الأطْنابَةِ أحدُ بَني الْخزْرَج ', 284, ' إنِّي مِنَ القَوْمِ الَّذينَ إذا انْتَدَوْا ', ' بَدْؤُا بحَقِّ اللهِ ثمَّ النَّائلُ )', 1),
('وقالت حبيبة بنت عبد العزى العوراء', 285, ' أُولِي عَلى هُلْكِ الطَّعامِ أليَّةً ', ' أبَداً وَلكنِّي أبِينُ وَأنْشُدُ )', 1),
('وقال عبد الله الحَواليّ منَ الأزد', 286, ' مُوَكَّلَةً بالأوَّلِينَ فَكلَّما ', ' رَأتْ رُفْقةً فالأوَّلُونَ لَها نُصْبُ )', 1),
('وقال آخرى', 287, ' نَصَبْنا لهُ جَوْفاءَ ذَاتَ ضَبابَةٍ ', ' منَ الدُّهْمِ مِبْطاناً طَويلاَ رُكُودُها )', 1),
('وقال آخر ', 288, ' ليس العطاء من الفضول سماحة ', ' حتى تجود وما لديك قليل )', 1),
('وقال عروة بن الورد تقدمت ترجمته', 289, ' إنِّي امرُوءٌ عافِي إناَئِيَ شِرْكةٌ ', ' وَأنْتَ امرُوءٌ عافِي إنائكَ وَاحدُ )', 1),
('قالَ المُثلَّمُ بنُ رياحٍ المُرِّي ', 290, ' وقُتودٍ ناجِيةٍ وضَعْتُ بِقفْرَةٍ ', ' والطَّيرُ غاشِيَةُ العَوَافي وُقَّعُ )', 1),
('وقال المساور بن هند بن قيس بن زهير تقدمت ترجمته', 291, ' فِدًا لِبني هِنْد غَدَاةَ دَعَوْتُهُمْ ', ' بِجَوَّ وبَالَ النَّفْسُ وَالأبَوَانِ )', 1),
('قال التبريزي هذه الأبيات لحاتم الطائي يخاطب امرأته ماوية بنت عبد الله ', 292, ' أيا ابْنةَ عَبْدِ اللهِ وَابنَةَ مالِكٍ ', ' ويا ابنةَ ذِي البُرْدَيْنِ والفَرَسِ الْوَرْد )', 1),
('وقال حزَازُ بنُ عمرٍو من بني عبد مناف', 293, ' ولَمْ تَكُ يَوْمًا إذَا رُوِّحَتْ ', ' عَلى الْحيِّ يُلْفَى لَها جادِبُ )', 1),
('وقال زيد الفوارس بن حصين بن ضررا تقدمت ترجمته', 294, ' أقِلي عليَّ اللَّوْمَ يَا ابنةَ مُنْذِرٍ ', ' وَنامِي فإنْ لَمْ تَشْتهِي النَّوْمَ فاسْهرِي )', 1),
('وقال الهُذَيلُ بن مَشجعةَ البُولانِيّ', 295, ' وَإذا أتَى مِنْ وِجهَةٍ بِطَريفَةٍ ', ' لمْ أطّلعْ مِمّا وَراءَ خِبائِهِ )', 1),
('وقالُ إيَاسُ بنُ الأَرَتّ', 296, ' وإنِّي لَقوَّالٌ لِعافيَّ مَرْحباً ', ' ولِلطَّالِبِ المَعْرُوفَ إنُّكَ واَجِدُهْ )', 1),
('وقال حسّان بن ثابت رضي الله عنه تقدمت ترجمته', 297, ' المالُ يَغْشَي رِجَالاً لاَ طباخَ بهِمْ ', ' كالسَّيْلِ يَغْشَى أُصُولَ الدِّنْدِنِ البَالي )', 1),
('وقال آخر ', 298, ' وَسِّعْ بمَدِّكَ ماءَ اللَّحْمِ تَقْسِمُهُ ', ' وَأكْثرِ الشَّوْبَ إنْ لمْ يَكثُرِ اللَّبَنُ )', 1),
('وقال حِماسُ بنُ ثامِل ', 299, ' ومسْتَنْبحٍ في لُجِّ لَيلٍ دَعَوْتُهُ ', ' بمَشْبُوبَةٍ في رَأسِ صَمْدٍ مُقابِلِ )', 1),
('وقال الفرزدق تقدمت ترجمته', 300, ' دَعا وَهْوَ يَرْجُو أنْ يُنَبِّهَ إذ دَعا ', ' فتًى كابْن لَيلى حينَ غارَتْ نُجُومُها )', 1),
('قال شرَيّحُ بنُ الأحوَصِ بن جعفر بن كلاب ', 301, ' فَاتَ وإنْ أسرَى منَ اللَّيلِ عُقْبةً ', ' بِلَيْلةِ صِدْقٍ غابَ عَنْها شُرُ برُها )', 1),
('وقال جابر بن حيان', 302, ' وَما وجَدَ الأضْيافُ فِيما يَنْوبُهُمْ ', ' لهُمْ عِنْدَ عِلاَّتِ الزَّمانِ أباً مِثْلي )', 1),
('وقال أيضاً', 303, ' وَإنِّي لأَسْتَحيِي يمِيني وَبيْنَها ', ' وبَينَ فَمي دَاجى الظَّلاَمِ بَهيمُ )', 1),
('وقال عُتبةُ بنُ بُجير وقيل أنها لمسكين الدراميّ', 304, ' أُحِدِّثُه إنَّ الْحَدِيثَ منَ القِرى ', ' وتَعْلَمُ نَفْسِي أنَّه سوْفَ يَهْجَعُ )', 1),
('وقال المَرَّار الفَقْغسي تقدمت ترجمته', 305, ' إذا قالَ مَنْ أنْتُمْ لِيَعرَفَ أهْلَها ', ' رَفَعْتُ لهُ باسْمِي ولَمْ أتَنكَّرِ )', 1),
('وقالَ الأقرعُ بنُ مُعاذ', 306, ' تُسلِّفُ الْجَارَ شِرْباً وهْيَ حائمَةٌ ', ' ولاَ يَبِيتُ عَلى أعْناقِها قَسَمُ )', 1),
('وَقال سَوَادةُ اليربُوعيُّ', 307, ' لاَ أحْبِسُ المَالَ إلاّ رَيْثَ أُتْلِفُهُ ', ' وَلا تُغيِّرُني حالٌ إلى حَالِ )', 1),
('وقال المقَّنع الكندِيُّ تقدمت ترجمته', 308, ' لَيسَ العَطاءُ منَ الفَضُولِ سَماحَةً ', ' حتَّى تَجُودَ وَما لدَيْكَ قلِيلُ )', 1),
('وقال عبدُ الله بنُ الْحَشْرَج الجعدي ', 309, ' وتَربِيَتي الصَّغيرَ إِلى مَدَاهُ ', ' وتَأمِيلي هِلاَلاً عَنْ هَلاَلِ )', 1),
('وقال عارِق الطائيُّ تقدمت ترجمته', 310, ' ألا حيِّ قبْلَ البيْنِ منْ أنت عاشِقُهْ ', ' ومنْ أنت مُشْتاقٌ إليه وشائقُهْ )', 1),
('وقال مِلحة الجرمي', 311, ' إذا مَا رَمى أصْحابُهُ بجَبينه ', ' سُرَى اللَّيْلةِ الظَّلْماءِ لمْ يَتهكَّم )', 1),
('وقال يزيد الحارثي', 312, ' وإذا الفَتى لاقَى الْحِمامَ رَأيْتهُ ', ' لوْلاَ الثَّناءُ كأنَّهُ لَمْ يُولَدِ )', 1),
('وقال أبو تمام لما أتى يزيد بن عبد الملك بآل المهلب قام كثير بين يدي يزيد فقال ', 313, ' أساؤا فإنْ تَغْفرْ فإِنَّكَ أهلُهُ ', ' وأفْضَلُ حِلمٍ حِسْبةً حِلْمُ مُغضَبِ )', 1),
('قال ابن المولى ليزيد بن حاتم بن قبيصة بن المهلب ', 314, ' وإذا صَنعْتَ صَنِيعةً أتْمَمتَها ', ' بيَدَيْنِ لِيسَ نَداهُما بِمُكَدَّرِ )', 1),
('قال خلف بن خليفة مولى قيس بن ثعلبة ', 315, ' هو اللص وابن اللص لا لص مثله ', ' لنقب البيوت أو لطر الدراهم )', 1),
('وقال طُريْحُ بن إسماعيلَ الثّقفيّ ', 316, ' فأرْجِعُ مَغبُوطاً وَترْجِعُ بالّتي ', ' لَها أوَّلٌ في المَكرُمَاتِ وآخِرُ )', 1),
('وقال أعشى بني أبي رَبيعة ', 317, ' وما أنا في أمري ولا في خصومتي ', ' ) الأبيات فلما فرغ قال عبد الملك من يلومني على هذا وأمر له بصلة كبيرة', 1),
('وقال نصيب في عمر بن عبيد الله بن معمر التيمي تقدمت ترجمته', 318, ' أيَوْمٌ إذا ألْفَيْتَهُ ذا يَسارَةٍ ', ' فأعْطَيْتَ عَفْواً مِنكَ أمْ يَوْمُ تُجْهَدُ )', 1),
('وقال ابن عبدل الأسدي تقدمت ترجمته', 319, ' فإِذا ابْنُ بِشْرٍ في مَواكِبِه ', ' تَهْوِي بِهِ خَطَّارَةٌ سُرُحُ )', 1),
('وقالت قتيلة أخت النضر بن الحرث تقدمت ترجمتها', 320, ' الوَاهِبُ الألْفَ لاَ يبْغِي بهَا بدَلاً ', ' إلاّ الإِلهَ وَمعْرُوفاً بمَا اصْطنَعا )', 1),
('وقالت أخرى', 321, ' منْ كُلِّ مَحْبوكٍ طُوالِ القَرَى ', ' مِثْل سِنانِ الرُّمْحِ مَشْهومِ )', 1),
('وقالت امرأة من إياد', 322, ' لاَ يرْهَبُ الجَارُ مِنهُ غَدْرَةً أبداً ', ' وإنْ ألمَّتْ أُمورٌ فهْوَ كافيهَا )', 1),
('وقال عَنترَةُ بنُ الأَخرَسِ', 323, ' كأنَّ بِضاحِي جِلْدِهِ وسَراتِهِ ', ' ومَجْمَعِ لِيتَيْهِ تَهاويلَ زُخْرُفِ )', 1),
('وقال آخر', 324, ' دَعَوْتُ فتًى أجَاب فتًى دَعاهُ ', ' بِلَبَّيْهِ أشَمَّ شَمَرْدَليِّ )', 1),
('وقال آخر', 325, ' نَكادُ إذا قُمْنا يُطِيرُ قُلوبَنا ', ' تَسَرْبُلُنا وَلوْثُنا بالعَصائبِ )', 1),
('وقال حكيمُ بنُ قبيصَةَ الضبيّ ) ', 326, ' فَما جَنةَ الفِرْدَوْسِ هَاجَرْتَ تَبْتَغي ', ' ولكنْ دَعاكَ الْخُبْزُ أحسَبُ والتّمرُ )', 1),
('وقال حُندُجُ بنُ حُندُجِ المُرِّيّ', 327, ' لِساهِر طالَ فِي صُولٍ تَمَلْمُلُهُ ', ' كأَنّهُ حيَّةٌ بِالسَّوطِ مَقْتولُ )', 1),
('قالية مبغضة والمعنى أن نساء الرجال الطاعنين في السن في غم وكرب يتمنين مفارقتهم ويبغضن مصاحبتهم لما يجدنه من نكد العيش وضيقه ', 328, ' نَكَحْتُ المَدِينِيَّ إذْ جَاءَني ', ' فيَالَكِ منْ نَكحةٍ غاليَهْ )', 1),
('وقال آخر ومر بأبي العلاء العقيلي يفلي ثيابه', 329, ' وكأنَّهُنَّ لدَى دُرُوزِ قَمِيصهِ ', ' فذٌّ وَتوْأمُ سِمْسِمٍ مَقْشورِ )', 1),
('وقال آخر وقد سُرِقَتْ له دَلوٌ ', 330, ' أفيضُوا عَلى عُزَّابِكُمْ بنِسائِكُمْ ', ' فَما فِي كتابِ اللهِ أنْ يَحْرُمَ الفَضلُ )', 1),
('وقال آخر ', 331, ' من يشتري مني زوجا خبا ', ' أخب من ضب يداهي ضبا )', 1),
('وقال آخر ', 332, ' أنِخْ فَاصْطَبِغْ قرْصاً إذا اعْتادَكَ الهَوَى ', ' بِزَيْتٍ كمَا يكْفيكَ فقْدَ الْحَبائِبِ )', 1),
('وقال آخر ', 333, ' وقال بِلالَ بنُ جرير )', ' حتى أقُولَ لعَلَّ الضَّيْفَ قَدْ وَلدَا )', 1),
('وقال أعرابي لابنه وكان قد دخل الحمام فأحرقته النورة', 334, ' أجِدَّكُما لَمْ تَعْلَما أنّ جَارَنا ', ' أبا الحِسلِ بالصّحْرَاءِ لاَ يَتَنوَّرُ )', 1),
('وقالت أخرى ', 335, ' [لَعَمرِي]لَقدْ أخْلَفتَ ظَنِّي وسُؤتَنِي ', ' فَحُزْتَ بِعصْيانِي النَّدَامةَ فَاصْبِر )', 1),
('وقال سعد ابنها وليس من الكتاب', 336, ' ليْستْ بشَبْعَى وَلو أوْرَدْتَها هجَراً ', ' ولا بِرَيّا ولوْ قاظَتْ بذِي قارِ )', 1),
('قال بعضهم ', 337, ' فإن أنفلت من عمر صعبة سالما ', ' تكن من نساء الناس في بيضة العقر )', 1),
('وقال آخر ', 338, ' تَمَّتْ عُبَيْدَةُ إلاَّ مِنْ مَحَاسِنِها ', ' والْمِلْحُ منْها مكانَ الشَّمْس والْقَمرِ )', 1),
('وقال آخر ', 339, ' رَقْطاءُ حَدْباءُ يُبْدِي الكِبْدَ مضْحكُها ', ' قنوْاءُ بالْعَرْض والعَينَانِ بالطُولِ )', 1),
('وقال آخر', 340, ' وإنْ حَدَّثَتْ كانَتْ جميعَ مَصائِبٍ ', ' مُوَفَّرَةٍ تأتِي بِقاصِمَةِ الظهرِ )', 1),
('وقال آخر ', 341, ' عَلى نَغانِغَ سَالتْ في بَلاعِمِهَا ', ' كَثِيرَةِ الوَشْيِ في لِينٍ وتَرْقِيق )', 1);
